// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import "../interfaces/IExecutor.sol";
import "../interfaces/IAddressesProvider.sol";
import "../interfaces/IRoleManager.sol";
import "../interfaces/IOrderManager.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/IIndexPriceFeed.sol";
import "hardhat/console.sol";

contract Executor is IExecutor {

    uint256 public increaseMarketOrderStartIndex;
    uint256 public decreaseMarketOrderStartIndex;

    IAddressesProvider public immutable ADDRESS_PROVIDER;

    IOrderManager public orderManager;
    IPositionManager public positionManager;

    constructor(IAddressesProvider addressProvider, IOrderManager _orderManager, IPositionManager _positionManager) {
        ADDRESS_PROVIDER = addressProvider;
        orderManager = _orderManager;
        positionManager = _positionManager;
    }

    modifier onlyPoolAdmin() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isPoolAdmin(msg.sender), "onlyPoolAdmin");
        _;
    }

    modifier onlyPositionKeeper() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isKeeper(msg.sender), "onlyPositionKeeper");
        _;
    }

    function setPricesAndExecuteMarketOrders(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        uint256 increaseEndIndex,
        uint256 decreaseEndIndex
    ) external onlyPositionKeeper {
        require(tokens.length == prices.length && tokens.length >= 0, "invalid params");

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        this.executeIncreaseMarketOrders(increaseEndIndex);
        this.executeDecreaseMarketOrders(decreaseEndIndex);
    }

    function setPricesAndExecuteLimitOrders(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        uint256[] memory increaseOrderIds,
        uint256[] memory decreaseOrderIds
    ) external onlyPositionKeeper {
        require(tokens.length == prices.length && tokens.length >= 0, "invalid params");

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        this.executeIncreaseLimitOrders(increaseOrderIds);
        this.executeDecreaseLimitOrders(decreaseOrderIds);
    }

    function executeIncreaseMarketOrders(uint256 endIndex) external onlyPositionKeeper {
        console.log("executeIncreaseMarketOrders endIndex", endIndex, "timestamp", block.timestamp);
        uint256 index = increaseMarketOrderStartIndex;
        uint256 length = orderManager.increaseMarketOrdersIndex();

        if (index >= length) {
            return;
        }
        if (endIndex > length) {
            endIndex = length;
        }

        while (index < endIndex) {
            try positionManager.executeIncreaseOrder(index, TradingTypes.TradeType.MARKET) {
                console.log();
            } catch Error(string memory reason) {
                console.log("executeIncreaseMarketOrder error ", reason);
                orderManager.cancelOrder(index, TradingTypes.TradeType.MARKET, true);
            }
            increaseMarketOrderStartIndex++;
        }
    }

    function executeIncreaseLimitOrders(uint256[] memory orderIds) external onlyPositionKeeper {
        console.log("executeIncreaseLimitOrders timestamp", block.timestamp);

        for (uint256 i = 0; i < orderIds.length; i++) {
            try positionManager.executeIncreaseOrder(orderIds[i], TradingTypes.TradeType.LIMIT) {
                console.log();
            } catch Error(string memory reason) {
                console.log("executeIncreaseLimitOrders error ", reason);
            }
        }
    }

    function executeIncreaseOrder(uint256 orderId, TradingTypes.TradeType tradeType) external onlyPositionKeeper {
        positionManager.executeIncreaseOrder(orderId, tradeType);
    }

    function executeDecreaseMarketOrders(uint256 endIndex) external onlyPositionKeeper {
        console.log("executeDecreaseMarketOrders endIndex", endIndex, "timestamp", block.timestamp);
        uint256 index = decreaseMarketOrderStartIndex;
        uint256 length = orderManager.decreaseMarketOrdersIndex();
        if (index >= length) {
            return;
        }
        if (endIndex > length) {
            endIndex = length;
        }

        while (index < endIndex) {
            try positionManager.executeDecreaseOrder(index, TradingTypes.TradeType.MARKET) {
                console.log("executeDecreaseMarketOrders success index", index, "endIndex", endIndex);
            } catch Error(string memory reason) {
                console.log("executeDecreaseMarketOrders error ", reason);
                orderManager.cancelOrder(index, TradingTypes.TradeType.MARKET, false);
            }
            decreaseMarketOrderStartIndex++;
        }
    }

    function executeDecreaseLimitOrders(uint256[] memory orderIds) external onlyPositionKeeper {
        console.log("executeDecreaseLimitOrders timestamp", block.timestamp);

        for (uint256 i = 0; i < orderIds.length; i++) {
            try positionManager.executeDecreaseOrder(orderIds[i], TradingTypes.TradeType.LIMIT) {
                console.log("executeDecreaseLimitOrders success index", orderIds[i]);
            } catch Error(string memory reason) {
                console.log("executeDecreaseLimitOrders error ", reason);
            }
        }
    }

    function executeDecreaseOrder(uint256 orderId, TradingTypes.TradeType tradeType) external onlyPositionKeeper {
        positionManager.executeDecreaseOrder(orderId, tradeType);
    }

    function setPricesAndLiquidatePositions(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        bytes32[] memory positionKeys
    ) external onlyPositionKeeper {
        require(tokens.length == prices.length && tokens.length >= 0, "invalid params");

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        positionManager.liquidatePositions(positionKeys);
    }

    function liquidatePositions(bytes32[] memory positionKeys) external onlyPositionKeeper {
        positionManager.liquidatePositions(positionKeys);
    }

    function setPricesAndExecuteADL(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        bytes32[] memory positionKeys,
        uint256[] memory sizeAmounts,
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) external onlyPositionKeeper {
        require(tokens.length == prices.length && tokens.length >= 0, "invalid params");

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        positionManager.executeADLAndDecreaseOrder(positionKeys, sizeAmounts, orderId, tradeType);
    }

    function executeADLAndDecreaseOrder(
        bytes32[] memory positionKeys,
        uint256[] memory sizeAmounts,
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) public onlyPositionKeeper {
        positionManager.executeADLAndDecreaseOrder(
            positionKeys,
            sizeAmounts,
            orderId,
            tradeType
        );
    }

}

contracts/trading/OrderManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "../interfaces/IVaultPriceFeed.sol";
import "../pair/interfaces/IPairInfo.sol";
import "../pair/interfaces/IPairVault.sol";
import "../libraries/PrecisionUtils.sol";
import "../libraries/PositionKey.sol";
import "../libraries/Int256Utils.sol";
import "../libraries/Handleable.sol";
import "../libraries/type/TradingTypes.sol";

import "../interfaces/ITradingVault.sol";
import "hardhat/console.sol";
import "../interfaces/IOrderManager.sol";
import "../interfaces/IAddressesProvider.sol";
import "../interfaces/IRoleManager.sol";
import "../interfaces/IPositionManager.sol";

contract OrderManager is IOrderManager, ReentrancyGuardUpgradeable, Handleable {
    using SafeERC20 for IERC20;
    using PrecisionUtils for uint256;
    using Math for uint256;
    using Int256Utils for int256;
    using Position for mapping(bytes32 => Position.Info);
    using Position for Position.Info;

    mapping(uint256 => TradingTypes.IncreasePositionOrder) public increaseMarketOrders;
    uint256 public override increaseMarketOrdersIndex;

    mapping(uint256 => TradingTypes.DecreasePositionOrder) public decreaseMarketOrders;
    uint256 public override decreaseMarketOrdersIndex;

    mapping(uint256 => TradingTypes.IncreasePositionOrder) public increaseLimitOrders;
    uint256 public override increaseLimitOrdersIndex;

    mapping(uint256 => TradingTypes.DecreasePositionOrder) public decreaseLimitOrders;
    uint256 public override decreaseLimitOrdersIndex;

    mapping(bytes32 => PositionOrder[]) public positionOrders;
    mapping(bytes32 => mapping(bytes32 => uint256)) public positionOrderIndex;

    mapping(bytes32 => uint256) public positionDecreaseTotalAmount;

    mapping(bytes32 => mapping(TradingTypes.TradeType => bool)) public positionHasTpSl; // PositionKey -> TradeType -> bool

    IPairInfo public pairInfo;
    IPairVault public pairVault;
    ITradingVault public tradingVault;
    IVaultPriceFeed public vaultPriceFeed;
    IPositionManager public positionManager;

    constructor(
        IAddressesProvider addressProvider,
        IPairInfo _pairInfo,
        IPairVault _pairVault,
        ITradingVault _tradingVault,
        IVaultPriceFeed _vaultPriceFeed
    ) Handleable(addressProvider) {
        pairInfo = _pairInfo;
        pairVault = _pairVault;
        tradingVault = _tradingVault;
        vaultPriceFeed = _vaultPriceFeed;
    }

    modifier onlyWhiteListOrSelf(address account) {
        require(
            IRoleManager(ADDRESS_PROVIDER.getRoleManager()).contractWhiteList(msg.sender) || account == msg.sender, "no access");
        _;
    }

    function getPositionOrders(bytes32 key) public view override returns (PositionOrder[] memory) {
        return positionOrders[key];
    }

    function updatePositionManager(address newAddress) external onlyPoolAdmin {
        address oldAddress = address(positionManager);
        positionManager = IPositionManager(newAddress);
        emit UpdatePositionManager(oldAddress, newAddress);
    }

    function createOrder(
        TradingTypes.CreateOrderRequest memory request
    ) public nonReentrant onlyWhiteListOrSelf(request.account) returns (uint256 orderId) {
        require(address(positionManager) != address(0), "zero address");

        address account = request.account;
        require(!tradingVault.isFrozen(account), "account is frozen");

        IPairInfo.Pair memory pair = pairInfo.getPair(request.pairIndex);
        require(pair.enable, "trade pair not supported");

        if (request.tradeType == TradingTypes.TradeType.MARKET || request.tradeType == TradingTypes.TradeType.LIMIT) {
            // check size
            require(request.sizeAmount == 0 || _checkTradingAmount(request.pairIndex, request.sizeAmount.abs()), "invalid trade size");

            bytes32 positionKey = PositionKey.getPositionKey(account, request.pairIndex, request.isLong);
            Position.Info memory position = tradingVault.getPosition(account, request.pairIndex, request.isLong);
            uint256 price = vaultPriceFeed.getPrice(pair.indexToken);
            IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(position.pairIndex);
            //TODO if size = 0
            if (request.sizeAmount >= 0) {
                // check leverage
                (uint256 afterPosition,) = position.validLeverage(price, request.collateral, uint256(request.sizeAmount), true, tradingConfig.minLeverage, tradingConfig.maxLeverage, tradingConfig.maxPositionAmount);
                // (uint256 afterPosition,) = tradingUtils.validLeverage(account, request.pairIndex, request.isLong, request.collateral, uint256(request.sizeAmount), true);
                require(afterPosition > 0, "zero position amount");

                // check tp sl
                require(request.tp <= afterPosition && request.sl <= afterPosition, "tp/sl exceeds max size");
                require(request.tp == 0 || !positionHasTpSl[positionKey][TradingTypes.TradeType.TP], "tp already exists");
                require(request.sl == 0 || !positionHasTpSl[positionKey][TradingTypes.TradeType.SL], "sl already exists");
            }
            if (request.sizeAmount <= 0) {
                // check leverage
                position.validLeverage(price, request.collateral, uint256(request.sizeAmount.abs()), false, tradingConfig.minLeverage, tradingConfig.maxLeverage, tradingConfig.maxPositionAmount);

                //TODO if request size exceed position size, can calculate the max size
                require(uint256(request.sizeAmount.abs()) <= position.positionAmount - positionDecreaseTotalAmount[positionKey], "decrease amount exceed position");
            }

            // transfer collateral
            if (request.collateral > 0) {
                IERC20(pair.stableToken).safeTransferFrom(account, address(positionManager), request.collateral.abs());
            }
        }

        if (request.tradeType == TradingTypes.TradeType.TP || request.tradeType == TradingTypes.TradeType.SL) {
            Position.Info memory position = tradingVault.getPosition(account, request.pairIndex, request.isLong);
            require(request.tp <= position.positionAmount && request.sl <= position.positionAmount, "tp/sl exceeds max size");
            require(request.collateral == 0, "no collateral required");
        }

        if (request.sizeAmount > 0) {
            return _createIncreaseOrder(
                TradingTypes.IncreasePositionRequest({
                    account: account,
                    pairIndex: request.pairIndex,
                    tradeType: request.tradeType,
                    collateral: request.collateral,
                    openPrice: request.openPrice,
                    isLong: request.isLong,
                    sizeAmount: uint256(request.sizeAmount),
                    tpPrice: request.tpPrice,
                    tp: request.tp,
                    slPrice: request.slPrice,
                    sl: request.sl
                })
            );
        } else if (request.sizeAmount < 0) {
            return _createDecreaseOrder(
                TradingTypes.DecreasePositionRequest({
                    account: account,
                    pairIndex: request.pairIndex,
                    tradeType: request.tradeType,
                    collateral: request.collateral,
                    triggerPrice: request.openPrice,
                    sizeAmount: uint256(request.sizeAmount.abs()),
                    isLong: request.isLong
                })
            );
        } else {
            //todo
            revert('size eq 0');
        }
        return 0;
    }

    function cancelOrder(uint256 orderId, TradingTypes.TradeType tradeType, bool isIncrease) public nonReentrant {
        console.log("cancelIncreaseOrder orderId", orderId, "tradeType", uint8(tradeType));
        console.log("cancelIncreaseOrder orderId", orderId, "isIncrease", isIncrease);

        if (isIncrease) {
            TradingTypes.IncreasePositionOrder memory order = getIncreaseOrder(orderId, tradeType);
            if (order.account == address(0)) {
                return;
            }
            require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).contractWhiteList(msg.sender) || order.account == msg.sender, "no access");

            _cancelIncreaseOrder(order);
        } else {
            TradingTypes.DecreasePositionOrder memory order = getDecreaseOrder(orderId, tradeType);
            if (order.account == address(0)) {
                return;
            }
            require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).contractWhiteList(msg.sender) || order.account == msg.sender, "no access");

            _cancelDecreaseOrder(order);
        }
    }

    function _checkTradingAmount(uint256 pairIndex, uint256 size) public returns (bool) {
        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(pairIndex);
        return size >= tradingConfig.minTradeAmount && size <= tradingConfig.maxTradeAmount;
    }

    function _createIncreaseOrder(TradingTypes.IncreasePositionRequest memory _request) internal returns (uint256) {
        TradingTypes.IncreasePositionOrder memory order = TradingTypes.IncreasePositionOrder(
            0,
            _request.account,
            _request.pairIndex,
            _request.tradeType,
            _request.collateral,
            _request.openPrice,
            _request.isLong,
            _request.sizeAmount,
            _request.tpPrice,
            _request.tp,
            _request.slPrice,
            _request.sl,
            block.timestamp
        );

        if (_request.tradeType == TradingTypes.TradeType.MARKET) {
            order.orderId = increaseMarketOrdersIndex;

            increaseMarketOrders[increaseMarketOrdersIndex++] = order;
        } else if (_request.tradeType == TradingTypes.TradeType.LIMIT) {
            order.orderId = increaseLimitOrdersIndex;

            increaseLimitOrders[increaseLimitOrdersIndex++] = order;
            console.log("orderId", order.orderId, "increaseLimitOrdersIndex", increaseLimitOrdersIndex);
        } else {
            revert("invalid trade type");
        }

        this.addOrderToPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                true,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            ));

        emit CreateIncreaseOrder(
            order.account,
            order.orderId,
            _request.pairIndex,
            _request.tradeType,
            _request.collateral,
            _request.openPrice,
            _request.isLong,
            _request.sizeAmount,
            _request.tpPrice,
            _request.tp,
            _request.slPrice,
            _request.sl
        );
        return order.orderId;
    }

    function _createDecreaseOrder(TradingTypes.DecreasePositionRequest memory _request) internal returns (uint256) {
        TradingTypes.DecreasePositionOrder memory order = TradingTypes.DecreasePositionOrder(
            0, // orderId
            _request.account,
            _request.pairIndex,
            _request.tradeType,
            _request.collateral,
            _request.triggerPrice,
            _request.sizeAmount,
            _request.isLong,
            false, // abovePrice
            block.timestamp,
            false
        );

        // abovePrice
        // marketlong: true,  short: false
        //  limitlong: false, short: true
        //     tplong: false, short: true
        //     sllong: true,  short: false
        if (_request.tradeType == TradingTypes.TradeType.MARKET) {
            order.orderId = decreaseMarketOrdersIndex;
            order.abovePrice = _request.isLong;

            decreaseMarketOrders[decreaseMarketOrdersIndex++] = order;
        } else if (_request.tradeType == TradingTypes.TradeType.LIMIT) {
            order.orderId = decreaseLimitOrdersIndex;
            order.abovePrice = !_request.isLong;

            decreaseLimitOrders[decreaseLimitOrdersIndex++] = order;
        } else if (_request.tradeType == TradingTypes.TradeType.TP) {
            order.orderId = decreaseLimitOrdersIndex;
            order.abovePrice = !_request.isLong;

            decreaseLimitOrders[decreaseLimitOrdersIndex++] = order;

            bytes32 positionKey = PositionKey.getPositionKey(_request.account, _request.pairIndex, _request.isLong);
            positionHasTpSl[positionKey][TradingTypes.TradeType.TP] = true;
        } else if (_request.tradeType == TradingTypes.TradeType.SL) {
            order.orderId = decreaseLimitOrdersIndex;
            order.abovePrice = _request.isLong;

            decreaseLimitOrders[decreaseLimitOrdersIndex++] = order;

            bytes32 positionKey = PositionKey.getPositionKey(_request.account, _request.pairIndex, _request.isLong);
            positionHasTpSl[positionKey][TradingTypes.TradeType.SL] = true;
        } else {
            revert("invalid trade type");
        }

        // add decrease order
        this.addOrderToPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                false,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            ));

        emit CreateDecreaseOrder(
            order.account,
            order.orderId,
            _request.tradeType,
            _request.collateral,
            _request.pairIndex,
            _request.triggerPrice,
            _request.sizeAmount,
            _request.isLong,
            order.abovePrice
        );
        return order.orderId;
    }

    function _cancelIncreaseOrder(TradingTypes.IncreasePositionOrder memory order) internal {
        IPairInfo.Pair memory pair = pairInfo.getPair(order.pairIndex);

        if (order.collateral > 0) {
            IERC20(pair.stableToken).safeTransfer(order.account, order.collateral.abs());
        }

        this.removeOrderFromPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                true,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            ));

        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            delete increaseMarketOrders[order.orderId];
        } else if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            delete increaseLimitOrders[order.orderId];
        }

        emit CancelIncreaseOrder(order.account, order.orderId, order.tradeType);
    }

    function _cancelDecreaseOrder(TradingTypes.DecreasePositionOrder memory order) internal {
        IPairInfo.Pair memory pair = pairInfo.getPair(order.pairIndex);

        if (order.collateral > 0) {
            IERC20(pair.stableToken).safeTransfer(order.account, order.collateral.abs());
        }

        this.removeOrderFromPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                false,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            ));

        bytes32 key = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);

        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            delete decreaseMarketOrders[order.orderId];
        } else if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            delete decreaseLimitOrders[order.orderId];
        } else {
            positionHasTpSl[key][order.tradeType] = false;
            delete decreaseLimitOrders[order.orderId];
        }

        emit CancelDecreaseOrder(order.account, order.orderId, order.tradeType);
    }

    function getIncreaseOrder(
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) public view returns (TradingTypes.IncreasePositionOrder memory order) {
        if (tradeType == TradingTypes.TradeType.MARKET) {
            order = increaseMarketOrders[orderId];
        } else if (tradeType == TradingTypes.TradeType.LIMIT) {
            order = increaseLimitOrders[orderId];
        } else {
            revert("invalid trade type");
        }
        return order;
    }

    function getDecreaseOrder(
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) public view returns (TradingTypes.DecreasePositionOrder memory order) {
        if (tradeType == TradingTypes.TradeType.MARKET) {
            order = decreaseMarketOrders[orderId];
        } else {
            order = decreaseLimitOrders[orderId];
        }
        return order;
    }

    function addOrderToPosition(PositionOrder memory order) public onlyHandler {
        bytes32 positionKey = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);
        bytes32 orderKey = PositionKey.getOrderKey(order.isIncrease, order.tradeType, order.orderId);
        positionOrderIndex[positionKey][orderKey] = positionOrders[positionKey].length;
        positionOrders[positionKey].push(order);
        console.log("positionOrders add orderId", order.orderId, "tradeType", uint8(order.tradeType));

        if (!order.isIncrease && (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT)) {
            positionDecreaseTotalAmount[positionKey] += order.sizeAmount;
        }
    }

    function removeOrderFromPosition(PositionOrder memory order) public onlyHandler {
        console.log("removeOrderFromPosition account %s orderId %s tradeType %s ", order.account, order.orderId, uint8(order.tradeType));
        bytes32 positionKey = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);
        bytes32 orderKey = PositionKey.getOrderKey(order.isIncrease, order.tradeType, order.orderId);

        uint256 index = positionOrderIndex[positionKey][orderKey];
        uint256 lastIndex = positionOrders[positionKey].length - 1;

        if (index < lastIndex) {
            // swap last order
            PositionOrder memory lastOrder = positionOrders[positionKey][positionOrders[positionKey].length - 1];
            bytes32 lastOrderKey = PositionKey.getOrderKey(lastOrder.isIncrease, lastOrder.tradeType, lastOrder.orderId);

            positionOrders[positionKey][index] = lastOrder;
            positionOrderIndex[positionKey][lastOrderKey] = index;
        }
        delete positionOrderIndex[positionKey][orderKey];
        positionOrders[positionKey].pop();
        console.log("positionOrders remove orderId", order.orderId, "tradeType", uint8(order.tradeType));

        if (!order.isIncrease && (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT)) {
            positionDecreaseTotalAmount[positionKey] -= order.sizeAmount;
        }
    }

    function setPositionHasTpSl(bytes32 key, TradingTypes.TradeType tradeType, bool has) public onlyHandler {
        positionHasTpSl[key][tradeType] = has;
    }

    function removeIncreaseMarketOrders(uint256 orderId) public onlyHandler {
        delete increaseMarketOrders[orderId];
    }

    function removeIncreaseLimitOrders(uint256 orderId) public onlyHandler {
        delete increaseLimitOrders[orderId];
    }

    function removeDecreaseMarketOrders(uint256 orderId) public onlyHandler {
        delete decreaseMarketOrders[orderId];
    }

    function removeDecreaseLimitOrders(uint256 orderId) public onlyHandler {
        delete decreaseLimitOrders[orderId];
    }

    function setOrderNeedADL(uint256 orderId, TradingTypes.TradeType tradeType, bool needADL) public onlyHandler {
        TradingTypes.DecreasePositionOrder storage order;
        if (tradeType == TradingTypes.TradeType.MARKET) {
            order = decreaseMarketOrders[orderId];
        } else {
            order = decreaseLimitOrders[orderId];
            require(order.tradeType == tradeType, "trade type not match");
        }
        order.needADL = needADL;
    }
}

contracts/trading/PositionManager.sol.content:
pragma solidity 0.8.17;


import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../interfaces/IIndexPriceFeed.sol";
import "../interfaces/IVaultPriceFeed.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/ITradingVault.sol";
import "../interfaces/IRoleManager.sol";

import "../libraries/Position.sol";
import "../libraries/Handleable.sol";
import "../libraries/PrecisionUtils.sol";
import "../libraries/Int256Utils.sol";
import "../pair/interfaces/IPairInfo.sol";
import "../pair/interfaces/IPairVault.sol";
import '../interfaces/IAddressesProvider.sol';
import "hardhat/console.sol";
import "../interfaces/IOrderManager.sol";

contract PositionManager is IPositionManager, ReentrancyGuard, Handleable {

    using SafeERC20 for IERC20;
    using PrecisionUtils for uint256;
    using Math for uint256;
    using Int256Utils for int256;
    using Position for mapping(bytes32 => Position.Info);
    using Position for Position.Info;

    uint256 public maxTimeDelay;

    IPairInfo public pairInfo;
    IPairVault public pairVault;
    ITradingVault public tradingVault;
    IIndexPriceFeed public fastPriceFeed;
    IVaultPriceFeed public vaultPriceFeed;
    IOrderManager public orderManager;

    constructor(
        IAddressesProvider addressProvider,
        IPairInfo _pairInfo,
        IPairVault _pairVault,
        ITradingVault _tradingVault,
        IVaultPriceFeed _vaultPriceFeed,
        IIndexPriceFeed _fastPriceFeed,
        uint256 _maxTimeDelay,
        IOrderManager _orderManager
    ) Handleable (addressProvider) {
        maxTimeDelay = _maxTimeDelay;
        pairInfo = _pairInfo;
        pairVault = _pairVault;
        tradingVault = _tradingVault;
        fastPriceFeed = _fastPriceFeed;
        vaultPriceFeed = _vaultPriceFeed;
        orderManager = _orderManager;
    }

    modifier onlyWhitelistOrKeeper() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).contractWhiteList(msg.sender)
        || IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isKeeper(msg.sender),
            "onlyKeeper");
        _;
    }

    function updateMaxTimeDelay(uint256 newMaxTimeDelay) external override onlyPoolAdmin {
        uint256 oldDelay = maxTimeDelay;
        maxTimeDelay = newMaxTimeDelay;
        emit UpdateMaxTimeDelay(oldDelay, newMaxTimeDelay);
    }

    function executeIncreaseOrder(uint256 _orderId, TradingTypes.TradeType _tradeType) public nonReentrant onlyWhitelistOrKeeper {
        console.log("executeIncreaseOrder account %s orderId %s tradeType %s", msg.sender, _orderId, uint8(_tradeType));

        TradingTypes.IncreasePositionOrder memory order = orderManager.getIncreaseOrder(_orderId, _tradeType);

        if (order.account == address(0)) {
            console.log("executeIncreaseOrder not exists", _orderId);
            return;
        }

        // expire
        if (_tradeType == TradingTypes.TradeType.MARKET) {
            require(order.blockTime + maxTimeDelay >= block.timestamp, "order expired");
        }

        // check pair enable
        uint256 pairIndex = order.pairIndex;
        IPairInfo.Pair memory pair = pairInfo.getPair(pairIndex);
        require(pair.enable, "trade pair not supported");

        // check account enable
        require(!tradingVault.isFrozen(order.account), "account is frozen");

        // check trading amount
        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(pairIndex);
        require(order.sizeAmount == 0 || (order.sizeAmount >= tradingConfig.minTradeAmount && order.sizeAmount <= tradingConfig.maxTradeAmount), "invalid trade size");

        // check price
        uint256 price = getValidPrice(pairIndex, order.isLong);
        if (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT) {
            require(order.isLong ? price.mulPercentage(PrecisionUtils.oneHundredPercentage() - tradingConfig.priceSlipP) <= order.openPrice
                : price.mulPercentage(PrecisionUtils.oneHundredPercentage() + tradingConfig.priceSlipP) >= order.openPrice, "not reach trigger price");
        } else {
            require(order.isLong ? price >= order.openPrice : price <= order.openPrice, "not reach trigger price");
        }

        // compare openPrice and oraclePrice
        if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            if (order.isLong) {
                price = order.openPrice.min(price);
            } else {
                price = order.openPrice.max(price);
            }
        }

        // get position
        Position.Info memory position = tradingVault.getPosition(order.account, order.pairIndex, order.isLong);

        uint256 sizeDelta = order.sizeAmount.mulPrice(price);
        console.log("executeIncreaseOrder sizeAmount", order.sizeAmount, "sizeDelta", sizeDelta);

        // check position and leverage
        (uint256 afterPosition,) = position.validLeverage(price, order.collateral, order.sizeAmount, true, tradingConfig.minLeverage, tradingConfig.maxLeverage, tradingConfig.maxPositionAmount);
        require(afterPosition > 0, "zero position amount");

        // check tp sl
        require(order.tp == 0 || !orderManager.positionHasTpSl(position.key, TradingTypes.TradeType.TP), "tp already exists");
        require(order.sl == 0 || !orderManager.positionHasTpSl(position.key, TradingTypes.TradeType.SL), "sl already exists");

        IPairVault.Vault memory lpVault = pairVault.getVault(pairIndex);

        int256 preNetExposureAmountChecker = tradingVault.netExposureAmountChecker(order.pairIndex);
        console.log("executeIncreaseOrder preNetExposureAmountChecker", preNetExposureAmountChecker.abs());
        if (preNetExposureAmountChecker >= 0) {
            if (order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                console.log("executeIncreaseOrder availableIndex", availableIndex);
                require(order.sizeAmount <= availableIndex, "lp index token not enough");
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                console.log("executeIncreaseOrder availableStable", availableStable);
                require(order.sizeAmount <= uint256(preNetExposureAmountChecker) + availableStable.divPrice(price), "lp stable token not enough");
            }
        } else {
            if (order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                console.log("executeIncreaseOrder availableIndex", availableIndex);
                require(order.sizeAmount <= uint256(- preNetExposureAmountChecker) + availableIndex, "lp index token not enough");
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                console.log("executeIncreaseOrder availableStable", availableStable);
                require(order.sizeAmount <= availableStable.divPrice(price), "lp stable token not enough");
            }
        }

        // transfer collateral
        if (order.collateral > 0) {
            IERC20(pair.stableToken).safeTransfer(address(tradingVault), order.collateral.abs());
        }
        (uint256 tradingFee, int256 fundingFee) = tradingVault.increasePosition(order.account, pairIndex, order.collateral, order.sizeAmount, order.isLong, price);

        orderManager.removeOrderFromPosition(
            IOrderManager.PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                true,
                order.tradeType,
                _orderId,
                order.sizeAmount
            ));

        if (order.tp > 0) {
            orderManager.createOrder(TradingTypes.CreateOrderRequest({
                account: order.account,
                pairIndex: order.pairIndex,
                tradeType: TradingTypes.TradeType.TP,
                collateral: 0,
                openPrice: order.tpPrice,
                isLong: order.isLong,
                sizeAmount: - int256(order.tp),
                tpPrice: 0,
                tp: 0,
                slPrice: 0,
                sl: 0
            }));
        }
        if (order.sl > 0) {
            orderManager.createOrder(TradingTypes.CreateOrderRequest({
                account: order.account,
                pairIndex: order.pairIndex,
                tradeType: TradingTypes.TradeType.SL,
                collateral: 0,
                openPrice: order.slPrice,
                isLong: order.isLong,
                sizeAmount: - int256(order.sl),
                tpPrice: 0,
                tp: 0,
                slPrice: 0,
                sl: 0
            }));
        }

        // delete order
        if (_tradeType == TradingTypes.TradeType.MARKET) {
            orderManager.removeIncreaseMarketOrders(_orderId);
        } else if (_tradeType == TradingTypes.TradeType.LIMIT) {
            orderManager.removeIncreaseLimitOrders(_orderId);
        }

        emit ExecuteIncreaseOrder(
            order.account,
            _orderId,
            pairIndex,
            _tradeType,
            order.collateral,
            order.isLong,
            order.sizeAmount,
            price,
            tradingFee,
            fundingFee
        );
    }

    function executeDecreaseOrder(uint256 _orderId, TradingTypes.TradeType _tradeType) external nonReentrant onlyWhitelistOrKeeper {
        _executeDecreaseOrder(_orderId, _tradeType);
    }

    function _executeDecreaseOrder(uint256 _orderId, TradingTypes.TradeType _tradeType) internal {
        console.log("executeDecreaseOrder account %s orderId %s tradeType %s", msg.sender, _orderId, uint8(_tradeType));

        TradingTypes.DecreasePositionOrder memory order = orderManager.getDecreaseOrder(_orderId, _tradeType);

        if (order.account == address(0)) {
            console.log("executeDecreaseOrder not exists", _orderId);
            return;
        }

        // expire
        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            require(order.blockTime + maxTimeDelay >= block.timestamp, "order expired");
        }

        // get pair
        uint256 pairIndex = order.pairIndex;
        IPairInfo.Pair memory pair = pairInfo.getPair(pairIndex);

        // get position
        Position.Info memory position = tradingVault.getPosition(order.account, order.pairIndex, order.isLong);
        if (position.positionAmount == 0) {
            console.log("position already closed", _orderId);
            return;
        }

        // check trading amount
        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(pairIndex);

        order.sizeAmount = order.sizeAmount.min(position.positionAmount);
        require(order.sizeAmount == 0 || (order.sizeAmount >= tradingConfig.minTradeAmount && order.sizeAmount <= tradingConfig.maxTradeAmount), "invalid trade size");

        // check price
        uint256 price = getValidPrice(pairIndex, order.isLong);
        if (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT) {
            require(order.abovePrice ? price.mulPercentage(PrecisionUtils.oneHundredPercentage() - tradingConfig.priceSlipP) <= order.triggerPrice
                : price.mulPercentage(PrecisionUtils.oneHundredPercentage() + tradingConfig.priceSlipP) >= order.triggerPrice, "not reach trigger price");
        } else {
            require(order.abovePrice ? price <= order.triggerPrice : price >= order.triggerPrice, "not reach trigger price");
        }

        // compare openPrice and oraclePrice
        if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            if (!order.isLong) {
                price = order.triggerPrice.min(price);
            } else {
                price = order.triggerPrice.max(price);
            }
        }

        uint256 sizeDelta = order.sizeAmount.mulPrice(price);
        console.log("executeDecreaseOrder sizeAmount", order.sizeAmount, "sizeDelta", sizeDelta);

        // check position and leverage
        position.validLeverage(price, order.collateral, order.sizeAmount, false, tradingConfig.minLeverage, tradingConfig.maxLeverage, tradingConfig.maxPositionAmount);

        IPairVault.Vault memory lpVault = pairVault.getVault(pairIndex);

        int256 preNetExposureAmountChecker = tradingVault.netExposureAmountChecker(order.pairIndex);
        console.log("executeDecreaseOrder preNetExposureAmountChecker", preNetExposureAmountChecker.toString());
        bool needADL;
        if (preNetExposureAmountChecker >= 0) {
            if (!order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                console.log("executeDecreaseOrder availableIndex", availableIndex);
                needADL = order.sizeAmount > availableIndex;
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                console.log("executeDecreaseOrder availableStable", availableStable);
                needADL = order.sizeAmount > uint256(preNetExposureAmountChecker) + availableStable.divPrice(price);
            }
        } else {
            if (!order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                console.log("executeDecreaseOrder availableIndex", availableIndex);
                needADL = order.sizeAmount > uint256(- preNetExposureAmountChecker) + availableIndex;
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                console.log("executeDecreaseOrder availableStable", availableStable);
                needADL = order.sizeAmount > availableStable.divPrice(price);
            }
        }

        if (needADL) {
            console.log("executeDecreaseOrder needADL");
            orderManager.setOrderNeedADL(_orderId, order.tradeType, needADL);

            emit ExecuteDecreaseOrder(
                order.account,
                _orderId,
                pairIndex,
                order.tradeType,
                order.isLong,
                order.sizeAmount,
                price,
                0,
                needADL,
                0,
                0
            );
            return;
        }

        // transfer collateral
        if (order.collateral > 0) {
            IPairInfo.Pair memory pair = pairInfo.getPair(position.pairIndex);
            IERC20(pair.stableToken).safeTransfer(address(tradingVault), order.collateral.abs());
        }
        (uint256 tradingFee, int256 fundingFee, int256 pnl) = tradingVault.decreasePosition(order.account, pairIndex, order.collateral, order.sizeAmount, order.isLong, price);

        // delete order
        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            orderManager.removeDecreaseMarketOrders(_orderId);
        } else if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            orderManager.removeDecreaseLimitOrders(_orderId);
        } else {
            orderManager.setPositionHasTpSl(position.key, order.tradeType, false);
            orderManager.removeDecreaseLimitOrders(_orderId);
        }

        // remove decrease order
        orderManager.removeOrderFromPosition(
            IOrderManager.PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                false,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            ));

        position = tradingVault.getPosition(order.account, order.pairIndex, order.isLong);

        if (position.positionAmount == 0) {
            // cancel all decrease order
            bytes32 key = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);
            IOrderManager.PositionOrder[] memory orders = orderManager.getPositionOrders(key);

            for (uint256 i = 0; i < orders.length; i++) {
                IOrderManager.PositionOrder memory positionOrder = orders[i];
                if (!positionOrder.isIncrease) {
                    orderManager.cancelOrder(positionOrder.orderId, positionOrder.tradeType, false);
                }
            }
        }

        emit ExecuteDecreaseOrder(
            order.account,
            _orderId,
            pairIndex,
            order.tradeType,
            order.isLong,
            order.sizeAmount,
            price,
            pnl,
            needADL,
            tradingFee,
            fundingFee
        );
    }

    function setPricesAndLiquidatePositions(
        address[] memory _tokens,
        uint256[] memory _prices,
        uint256 _timestamp,
        bytes32[] memory _positionKeys
    ) external onlyWhitelistOrKeeper {
        console.log("setPricesAndLiquidatePositions timestamp", block.timestamp);
        fastPriceFeed.setPrices(_tokens, _prices, _timestamp);
        this.liquidatePositions(_positionKeys);
    }

    function liquidatePositions(bytes32[] memory _positionKeys) external nonReentrant onlyWhitelistOrKeeper {
        for (uint256 i = 0; i < _positionKeys.length; i++) {
            _liquidatePosition(_positionKeys[i]);
        }
    }

    function executeADLAndDecreaseOrder(
        bytes32[] memory _positionKeys,
        uint256[] memory _sizeAmounts,
        uint256 _orderId,
        TradingTypes.TradeType _tradeType
    ) external nonReentrant onlyWhitelistOrKeeper {
        console.log("executeADLAndDecreaseOrder");

        require(_positionKeys.length == _sizeAmounts.length, "length not match");

        TradingTypes.DecreasePositionOrder memory order = orderManager.getDecreaseOrder(_orderId, _tradeType);
        require(order.needADL, "no need ADL");


        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(order.pairIndex);

        Position.Info[] memory adlPositions = new Position.Info[](_positionKeys.length);
        uint256 sumAmount;
        for (uint256 i = 0; i < _positionKeys.length; i++) {
            Position.Info memory position = tradingVault.getPositionByKey(_positionKeys[i]);
            require(_sizeAmounts[i] <= position.positionAmount, "ADL size exceeds position");
            require(_sizeAmounts[i] <= tradingConfig.maxTradeAmount, "exceeds max trade amount");
            sumAmount += _sizeAmounts[i];
            adlPositions[i] = position;
        }

        require(sumAmount == order.sizeAmount, "ADL position amount not match decrease order");
        IPairInfo.Pair memory pair = pairInfo.getPair(order.pairIndex);

        uint256 price = getValidPrice(order.pairIndex, !order.isLong);

        for (uint256 i = 0; i < adlPositions.length; i++) {
            Position.Info memory adlPosition = adlPositions[i];
            uint256 orderId = orderManager.createOrder(TradingTypes.CreateOrderRequest({
                account: adlPosition.account,
                pairIndex: adlPosition.pairIndex,
                tradeType: TradingTypes.TradeType.MARKET,
                collateral: 0,
                openPrice: price,
                isLong: adlPosition.isLong,
                sizeAmount: - int256(adlPosition.positionAmount),
                tpPrice: 0,
                tp: 0,
                slPrice: 0,
                sl: 0
            }));
            _executeDecreaseOrder(orderId, TradingTypes.TradeType.MARKET);
        }
        _executeDecreaseOrder(_orderId, order.tradeType);
    }

    function _liquidatePosition(bytes32 _positionKey) internal {
        console.log("liquidatePosition start");
        Position.Info memory position = tradingVault.getPositionByKey(_positionKey);

        if (position.positionAmount == 0) {
            console.log("position not exists");
            return;
        }

        uint256 price = getValidPrice(position.pairIndex, position.isLong);

        int256 unrealizedPnl;
        if (position.isLong) {
            if (price > position.averagePrice) {
                unrealizedPnl = int256(position.positionAmount.mulPrice(price - position.averagePrice));
            } else {
                unrealizedPnl = - int256(position.positionAmount.mulPrice(position.averagePrice - price));
            }
        } else {
            if (position.averagePrice > price) {
                unrealizedPnl = int256(position.positionAmount.mulPrice(position.averagePrice - price));
            } else {
                unrealizedPnl = - int256(position.positionAmount.mulPrice(price - position.averagePrice));
            }
        }
        console.log("liquidatePosition averagePrice %s unrealizedPnl %s", position.averagePrice, unrealizedPnl.toString());

        int256 exposureAsset = int256(position.collateral) + unrealizedPnl;
        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(position.pairIndex);

        bool needLiquidate;
        if (exposureAsset <= 0) {
            needLiquidate = true;
        } else {
            uint256 riskRate = position.positionAmount.mulPrice(price)
                .mulPercentage(tradingConfig.maintainMarginRate)
                .calculatePercentage(uint256(exposureAsset));
            needLiquidate = riskRate >= PrecisionUtils.oneHundredPercentage();
            console.log("liquidatePosition riskRate %s positionAmount %s exposureAsset %s", riskRate, position.positionAmount, exposureAsset.toString());
        }
        console.log("liquidatePosition needLiquidate", needLiquidate);

        if (!needLiquidate) {
            return;
        }

        //TODO positionManager
        // cancelAllPositionOrders
//        tradingRouter.cancelAllPositionOrders(position.account, position.pairIndex, position.isLong);

        uint256 orderId = orderManager.createOrder(TradingTypes.CreateOrderRequest({
            account: position.account,
            pairIndex: position.pairIndex,
            tradeType: TradingTypes.TradeType.MARKET,
            collateral: 0,
            openPrice: price,
            isLong: position.isLong,
            sizeAmount: - int256(position.positionAmount),
            tpPrice: 0,
            tp: 0,
            slPrice: 0,
            sl: 0
        }));

        _executeDecreaseOrder(orderId, TradingTypes.TradeType.MARKET);

        emit LiquidatePosition(
            _positionKey,
            position.account,
            position.pairIndex,
            position.isLong,
            position.positionAmount,
            position.collateral,
            price,
            orderId
        );
    }

    function getValidPrice(uint256 _pairIndex, bool _isLong) public view returns (uint256) {
        IPairInfo.Pair memory pair = pairInfo.getPair(_pairIndex);
        uint256 oraclePrice = vaultPriceFeed.getPrice(pair.indexToken);
        console.log("getValidPrice pairIndex %s isLong %s ", _pairIndex, _isLong);

        uint256 indexPrice = vaultPriceFeed.getIndexPrice(pair.indexToken, 0);
        console.log("getValidPrice oraclePrice %s indexPrice %s", oraclePrice, indexPrice);

        uint256 diffP = oraclePrice > indexPrice ? oraclePrice - indexPrice : indexPrice - oraclePrice;
        diffP = diffP.calculatePercentage(oraclePrice);

        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(_pairIndex);
        console.log("getValidPrice diffP %s maxPriceDeviationP %s", diffP, tradingConfig.maxPriceDeviationP);
        require(diffP <= tradingConfig.maxPriceDeviationP, "exceed max price deviation");
        return oraclePrice;
    }

}

contracts/trading/Router.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import "../interfaces/IRouter.sol";
import "../interfaces/IAddressesProvider.sol";
import "../interfaces/IRoleManager.sol";
import "../interfaces/IOrderManager.sol";
import "../libraries/PositionKey.sol";
import "hardhat/console.sol";

contract Router is IRouter {

    IAddressesProvider public immutable ADDRESS_PROVIDER;

    IOrderManager public orderManager;

    modifier onlyPoolAdmin() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isPoolAdmin(msg.sender), "onlyPoolAdmin");
        _;
    }

    constructor(IAddressesProvider addressProvider, IOrderManager _orderManager) {
        ADDRESS_PROVIDER = addressProvider;
        orderManager = _orderManager;
    }

    function createIncreaseOrder(TradingTypes.IncreasePositionRequest memory request) external returns (uint256) {
        //TODO decoupling tp sl

        return orderManager.createOrder(TradingTypes.CreateOrderRequest({
            account: request.account,
            pairIndex: request.pairIndex,
            tradeType: request.tradeType,
            collateral: request.collateral,
            openPrice: request.openPrice,
            isLong: request.isLong,
            sizeAmount: int256(request.sizeAmount),
            tpPrice: request.tpPrice,
            tp: request.tp,
            slPrice: request.slPrice,
            sl: request.sl
        }));
    }

    function createDecreaseOrder(TradingTypes.DecreasePositionRequest memory request) external returns (uint256) {
        return orderManager.createOrder(TradingTypes.CreateOrderRequest({
            account: request.account,
            pairIndex: request.pairIndex,
            tradeType: request.tradeType,
            collateral: request.collateral,
            openPrice: request.triggerPrice,
            isLong: request.isLong,
            sizeAmount: - int256(request.sizeAmount),
            tpPrice: 0,
            tp: 0,
            slPrice: 0,
            sl: 0
        }));
    }

    function cancelIncreaseOrder(uint256 orderId, TradingTypes.TradeType tradeType) external {
        orderManager.cancelOrder(orderId, tradeType, true);
    }

    function cancelDecreaseOrder(uint256 orderId, TradingTypes.TradeType tradeType) external {
        orderManager.cancelOrder(orderId, tradeType, false);
    }

    function cancelAllPositionOrders(uint256 pairIndex, bool isLong) external {
        bytes32 key = PositionKey.getPositionKey(msg.sender, pairIndex, isLong);
        IOrderManager.PositionOrder[] memory orders = orderManager.getPositionOrders(key);

        while (orders.length > 0) {
            uint256 lastIndex = orders.length - 1;
            IOrderManager.PositionOrder memory positionOrder = orders[lastIndex];
            console.log("positionOrder lastIndex", lastIndex, "orderId", positionOrder.orderId);
            console.log("positionOrder tradeType", uint8(positionOrder.tradeType), "isIncrease", positionOrder.isIncrease);
            if (positionOrder.isIncrease) {
                orderManager.cancelOrder(positionOrder.orderId, positionOrder.tradeType, true);
            } else {
                orderManager.cancelOrder(positionOrder.orderId, positionOrder.tradeType, false);
            }
        }
    }

    function cancelOrders(uint256 pairIndex, bool isLong, bool isIncrease) external {
        bytes32 key = PositionKey.getPositionKey(msg.sender, pairIndex, isLong);
        IOrderManager.PositionOrder[] memory orders = orderManager.getPositionOrders(key);

        for (uint256 i = 0; i < orders.length; i++) {
            IOrderManager.PositionOrder memory positionOrder = orders[i];
            console.log("positionOrder index", i, "orderId", positionOrder.orderId);
            console.log("positionOrder tradeType", uint8(positionOrder.tradeType), "isIncrease", positionOrder.isIncrease);
            if (isIncrease && positionOrder.isIncrease) {
                orderManager.cancelOrder(positionOrder.orderId, positionOrder.tradeType, true);
            } else if (!isIncrease && !positionOrder.isIncrease) {
                orderManager.cancelOrder(positionOrder.orderId, positionOrder.tradeType, false);
            }
        }
    }

    function createTpSl(TradingTypes.CreateTpSlRequest memory request) external returns (uint256 tpOrderId, uint256 slOrderId) {
        bytes32 key = PositionKey.getPositionKey(msg.sender, request.pairIndex, request.isLong);
        require(request.tp == 0 || !orderManager.positionHasTpSl(key, TradingTypes.TradeType.TP), "tp already exists");
        require(request.sl == 0 || !orderManager.positionHasTpSl(key, TradingTypes.TradeType.SL), "sl already exists");

        if (request.tp > 0) {
            tpOrderId = orderManager.createOrder(TradingTypes.CreateOrderRequest({
                account: msg.sender,
                pairIndex: request.pairIndex,
                tradeType: TradingTypes.TradeType.TP,
                collateral: 0,
                openPrice: request.tpPrice,
                isLong: request.isLong,
                sizeAmount: - int256(request.tp),
                tpPrice: 0,
                tp: 0,
                slPrice: 0,
                sl: 0
            }));
        }
        if (request.sl > 0) {
            slOrderId = orderManager.createOrder(TradingTypes.CreateOrderRequest({
                account: msg.sender,
                pairIndex: request.pairIndex,
                tradeType: TradingTypes.TradeType.SL,
                collateral: 0,
                openPrice: request.slPrice,
                isLong: request.isLong,
                sizeAmount: - int256(request.sl),
                tpPrice: 0,
                tp: 0,
                slPrice: 0,
                sl: 0
            }));
        }
        return (tpOrderId, slOrderId);
    }

}

contracts/trading/TradingVault.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "../libraries/Position.sol";
import "../libraries/PositionKey.sol";
import "../interfaces/ITradingVault.sol";
import "../interfaces/IVaultPriceFeed.sol";
import "../libraries/PrecisionUtils.sol";
import "../libraries/Int256Utils.sol";
import "../libraries/Handleable.sol";
import "../pair/interfaces/IPairInfo.sol";
import "../pair/interfaces/IPairVault.sol";
import "hardhat/console.sol";
import "../interfaces/IAddressesProvider.sol";
import "../interfaces/IRoleManager.sol";

contract TradingVault is ITradingVault, ReentrancyGuard, Handleable {
    using SafeERC20 for IERC20;
    using PrecisionUtils for uint256;
    using Math for uint256;
    using Int256Utils for int256;
    using Position for mapping(bytes32 => Position.Info);
    using Position for Position.Info;

    mapping(bytes32 => Position.Info) public positions;

    mapping(address => bool) public override isFrozen;

    mapping(uint256 => int256) public override netExposureAmountChecker;
    mapping(uint256 => uint256) public override longTracker;
    mapping(uint256 => uint256) public override shortTracker;

    // cumulativeFundingRates tracks the funding rates based on utilization
    mapping(uint256 => int256) public cumulativeFundingRates;
    mapping(uint256 => int256) public lastFundingRates;
    // lastFundingTimes tracks the last time funding was updated for a token
    mapping(uint256 => uint256) public lastFundingTimes;

    uint256 public fundingInterval;

    IPairInfo public pairInfo;
    IPairVault public pairVault;
    address public tradingFeeReceiver;
    IVaultPriceFeed public vaultPriceFeed;

    constructor (
        IAddressesProvider addressProvider,
        IPairInfo _pairInfo,
        IPairVault _pairVault,
        IVaultPriceFeed _vaultPriceFeed,
        address _tradingFeeReceiver,
        uint256 _fundingInterval
    ) Handleable(addressProvider) {
        pairInfo = _pairInfo;
        pairVault = _pairVault;
        vaultPriceFeed = _vaultPriceFeed;
        tradingFeeReceiver = _tradingFeeReceiver;
        fundingInterval = _fundingInterval;
    }

    function updatePairInfo(address newPairInfo) external onlyPoolAdmin {
        address oldPairInfo = address(pairInfo);
        pairInfo = IPairInfo(newPairInfo);
        emit UpdatePairInfo(oldPairInfo, newPairInfo);
    }

    function updatePairVault(address newPairVault) external onlyPoolAdmin {
        address oldPairVault = address(pairVault);
        pairVault = IPairVault(newPairVault);
        emit UpdatePairVault(oldPairVault, newPairVault);
    }

    function updateTradingFeeReceiver(address newReceiver) external onlyPoolAdmin {
        address oldReceiver = tradingFeeReceiver;
        tradingFeeReceiver = newReceiver;
        emit UpdateTradingFeeReceiver(oldReceiver, newReceiver);
    }

    function updateFundingInterval(uint256 newInterval) external onlyPoolAdmin {
        uint256 oldInterval = fundingInterval;
        fundingInterval = newInterval;
        emit UpdateFundingInterval(oldInterval, newInterval);
    }

    function increasePosition(
        address _account,
        uint256 _pairIndex,
        int256 _collateral,
        uint256 _sizeAmount,
        bool _isLong,
        uint256 _price
    ) external nonReentrant onlyHandler returns (uint256 tradingFee, int256 fundingFee) {

        IPairInfo.Pair memory pair = pairInfo.getPair(_pairIndex);
        require(pair.enable, "trade pair not supported");

        // get position
        bytes32 positionKey = PositionKey.getPositionKey(_account, _pairIndex, _isLong);
        Position.Info storage position = positions[positionKey];
        position.key = positionKey;

        uint256 sizeDelta = _sizeAmount.mulPrice(_price);
        console.log("increasePosition sizeAmount", _sizeAmount, "sizeDelta", sizeDelta);

        // 
        if (position.positionAmount == 0) {
            position.account = _account;
            position.pairIndex = _pairIndex;
            position.isLong = _isLong;
            position.averagePrice = _price;
        }

        if (position.positionAmount > 0 && sizeDelta > 0) {
            position.averagePrice = (position.positionAmount.mulPrice(position.averagePrice) + sizeDelta).mulDiv(PrecisionUtils.pricePrecision(), (position.positionAmount + _sizeAmount));
        }

        position.collateral = (int256(position.collateral) + _collateral).abs();
        position.positionAmount = position.positionAmount + _sizeAmount;

        uint256 transferOut = _collateral > 0 ? 0 : _collateral.abs();

        // funding fee
        updateCumulativeFundingRate(_pairIndex, _price);
        fundingFee = getFundingFee(true, _account, _pairIndex, _isLong, _sizeAmount);
        console.log("increasePosition lastFundingTimes", lastFundingTimes[_pairIndex]);
        console.log("increasePosition cumulativeFundingRates", cumulativeFundingRates[_pairIndex].toString());
        console.log("increasePosition fundingFee", fundingFee.toString());

        if (fundingFee >= 0) {
            uint256 absFundingFee = uint256(fundingFee);
            if (_isLong) {
                require(position.collateral >= absFundingFee, "collateral not enough for funding fee");
                position.collateral -= absFundingFee;
                console.log("increasePosition long pay funding fee");
            } else {
                transferOut += absFundingFee;  // todo distribute
                console.log("increasePosition long take funding fee");
            }
        } else {
            uint256 absFundingFee = uint256(- fundingFee);
            if (!_isLong) {
                require(position.collateral >= absFundingFee, "collateral not enough for funding fee");
                position.collateral = position.collateral - absFundingFee;
                console.log("increasePosition short pay funding fee");
            } else {
                transferOut += absFundingFee;  // todo distribute
                console.log("increasePosition short take funding fee");
            }
        }

        position.entryFundingRate = cumulativeFundingRates[_pairIndex];
        position.entryFundingTime = lastFundingTimes[_pairIndex];

        // trading fee
        tradingFee = _tradingFee(_pairIndex, _isLong, _sizeAmount, _price);
        require(position.collateral + transferOut >= tradingFee, "collateral not enough for trading fee");

        if (transferOut >= tradingFee) {

            transferOut -= tradingFee;
        } else {

            transferOut == 0;
            position.collateral -= tradingFee - transferOut;
        }
        // todo distribute
        IERC20(pair.stableToken).safeTransfer(tradingFeeReceiver, tradingFee);
        console.log("increasePosition tradingFee", tradingFee);


        int256 prevNetExposureAmountChecker = netExposureAmountChecker[_pairIndex];
        netExposureAmountChecker[_pairIndex] = prevNetExposureAmountChecker + (_isLong ? int256(_sizeAmount) : - int256(_sizeAmount));
        if (_isLong) {
            longTracker[_pairIndex] += _sizeAmount;
        } else {
            shortTracker[_pairIndex] += _sizeAmount;
        }

        console.log("increasePosition prevNetExposureAmountChecker", prevNetExposureAmountChecker.toString());
        console.log("increasePosition netExposureAmountChecker", netExposureAmountChecker[_pairIndex].toString());
        console.log("increasePosition longTracker", longTracker[_pairIndex], "shortTracker", shortTracker[_pairIndex]);


        IPairVault.Vault memory lpVault = pairVault.getVault(_pairIndex);
        console.log("increasePosition lp averagePrice", lpVault.averagePrice, "price", _price);
        if (prevNetExposureAmountChecker > 0) {

            if (netExposureAmountChecker[_pairIndex] > prevNetExposureAmountChecker) {
                console.log("increasePosition BTO long increase");
                pairVault.increaseReserveAmount(_pairIndex, _sizeAmount, 0);

                uint256 averagePrice = (uint256(prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) + sizeDelta)
                    .calculatePrice(uint256(prevNetExposureAmountChecker) + _sizeAmount);
                console.log("increasePosition BTO update averagePrice", averagePrice);
                pairVault.updateAveragePrice(_pairIndex, averagePrice);
            } else if (netExposureAmountChecker[_pairIndex] > 0) {

                console.log("increasePosition STO long decrease");
                pairVault.decreaseReserveAmount(_pairIndex, _sizeAmount, 0);

                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("increasePosition STO decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("increasePosition STO increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                }
            } else {
                console.log("increasePosition STO long to short");
                pairVault.decreaseReserveAmount(_pairIndex, lpVault.indexReservedAmount, 0);
                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("increasePosition STO decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("increasePosition STO increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                }

                pairVault.increaseReserveAmount(_pairIndex, 0, (_sizeAmount - uint256(prevNetExposureAmountChecker)).mulPrice(_price));
                console.log("increasePosition STO Long to Short update averagePrice", _price);
                pairVault.updateAveragePrice(_pairIndex, _price);
            }
        } else if (prevNetExposureAmountChecker < 0) {
            if (netExposureAmountChecker[_pairIndex] < prevNetExposureAmountChecker) {
                console.log("increasePosition STO short increase");
                pairVault.increaseReserveAmount(_pairIndex, 0, sizeDelta);

                uint256 averagePrice = (uint256(- prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) + sizeDelta)
                    .calculatePrice(uint256(- prevNetExposureAmountChecker) + _sizeAmount);
                console.log("increasePosition STO update averagePrice", averagePrice);
                pairVault.updateAveragePrice(_pairIndex, averagePrice);
            } else if (netExposureAmountChecker[_pairIndex] < 0) {
                console.log("increasePosition BTO short decrease");
                pairVault.decreaseReserveAmount(_pairIndex, 0, _sizeAmount.mulPrice(lpVault.averagePrice));

                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("increasePosition BTO decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("increasePosition BTO increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                }
            } else {
                console.log("increasePosition BTO short to long");
                pairVault.decreaseReserveAmount(_pairIndex, 0, lpVault.stableReservedAmount);
                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("increasePosition BTO increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("increasePosition BTO decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                }

                pairVault.increaseReserveAmount(_pairIndex, 0, (_sizeAmount - uint256(- prevNetExposureAmountChecker)).mulPrice(_price));
                console.log("increasePosition BTO Long to Short update averagePrice", _price);
                pairVault.updateAveragePrice(_pairIndex, _price);
            }
        } else {

            if (netExposureAmountChecker[_pairIndex] > 0) {
                console.log("increasePosition BTO zero to long");
                pairVault.increaseReserveAmount(_pairIndex, _sizeAmount, 0);
            } else {
                console.log("increasePosition STO zero to short");
                pairVault.increaseReserveAmount(_pairIndex, 0, sizeDelta);
            }
            pairVault.updateAveragePrice(_pairIndex, _price);
        }

        if (transferOut > 0) {
            IERC20(pair.stableToken).safeTransfer(_account, transferOut);
        }

        emit IncreasePosition(
            positionKey,
            _account,
            _pairIndex,
            _collateral,
            _isLong,
            _sizeAmount,
            _price,
            tradingFee,
            fundingFee,
            transferOut
        );

        emit UpdatePosition(
            positionKey,
            _account,
            _pairIndex,
            _isLong,
            position.collateral,
            position.positionAmount,
            position.averagePrice,
            position.entryFundingRate,
            position.entryFundingTime,
            position.realisedPnl,
            _price
        );

        console.log("increase position finish");
    }

    function decreasePosition(
        address _account,
        uint256 _pairIndex,
        int256 _collateral,
        uint256 _sizeAmount,
        bool _isLong,
        uint256 _price
    ) external onlyHandler nonReentrant returns (uint256 tradingFee, int256 fundingFee, int256 pnl) {

        IPairInfo.Pair memory pair = pairInfo.getPair(_pairIndex);

        // check trading amount
        IPairInfo.TradingConfig memory tradingConfig = pairInfo.getTradingConfig(_pairIndex);
        require(_sizeAmount >= tradingConfig.minTradeAmount && _sizeAmount <= tradingConfig.maxTradeAmount, "invalid size");

        // get position
        bytes32 positionKey = PositionKey.getPositionKey(_account, _pairIndex, _isLong);
        Position.Info storage position = positions[positionKey];
        require(position.account != address(0), "position already closed");

        uint256 sizeDelta = _sizeAmount.mulPrice(_price);
        console.log("decreasePosition sizeAmount", _sizeAmount, "sizeDelta", sizeDelta);

        // 
        if (position.positionAmount > 0 && sizeDelta > 0) {
            position.averagePrice = (position.positionAmount.mulPrice(position.averagePrice) + sizeDelta).mulDiv(PrecisionUtils.pricePrecision(), (position.positionAmount + _sizeAmount));
        }

        position.collateral = (int256(position.collateral) + _collateral).abs();
        position.positionAmount -= _sizeAmount;
        console.log("decreasePosition position collateral", position.collateral, "positionAmount", position.positionAmount);

        uint256 transferOut = _collateral > 0 ? 0 : _collateral.abs();

        // funding fee
        updateCumulativeFundingRate(_pairIndex, _price);
        fundingFee = getFundingFee(false, _account, _pairIndex, _isLong, _sizeAmount);
        console.log("decreasePosition lastFundingTimes", lastFundingTimes[_pairIndex]);
        console.log("decreasePosition cumulativeFundingRates", cumulativeFundingRates[_pairIndex].toString());
        console.log("decreasePosition fundingFee", fundingFee.toString());

        if (fundingFee >= 0) {
            uint256 absFundingFee = uint256(fundingFee);
            if (_isLong) {
                require(position.collateral >= absFundingFee, "collateral not enough for funding fee");
                position.collateral -= absFundingFee;
                console.log("decreasePosition long pay funding fee");
            } else {
                transferOut += absFundingFee;  // todo distribute
                console.log("decreasePosition long take funding fee");
            }
        } else {
            uint256 absFundingFee = uint256(- fundingFee);
            if (!_isLong) {
                require(position.collateral >= absFundingFee, "collateral not enough for funding fee");
                position.collateral = position.collateral - absFundingFee;
                console.log("decreasePosition short pay funding fee");
            } else {
                transferOut += absFundingFee;  // todo distribute
                console.log("decreasePosition short take funding fee");
            }
        }

        position.entryFundingRate = cumulativeFundingRates[_pairIndex];
        position.entryFundingTime = lastFundingTimes[_pairIndex];

        // trading fee
        tradingFee = _tradingFee(_pairIndex, _isLong, _sizeAmount, _price);
        require(position.collateral + transferOut >= tradingFee, "collateral not enough for trading fee");

        if (transferOut >= tradingFee) {

            transferOut -= tradingFee;
        } else {

            transferOut == 0;
            position.collateral -= tradingFee - transferOut;
        }
        // todo fee distribute
        IERC20(pair.stableToken).safeTransfer(tradingFeeReceiver, tradingFee);
        console.log("decreasePosition tradingFee", tradingFee);


        int256 prevNetExposureAmountChecker = netExposureAmountChecker[_pairIndex];
        netExposureAmountChecker[_pairIndex] = prevNetExposureAmountChecker + (_isLong ? - int256(_sizeAmount) : int256(_sizeAmount));
        if (_isLong) {
            longTracker[_pairIndex] -= _sizeAmount;
        } else {
            shortTracker[_pairIndex] -= _sizeAmount;
        }

        console.log("decreasePosition prevNetExposureAmountChecker", prevNetExposureAmountChecker.toString());
        console.log("decreasePosition netExposureAmountChecker", netExposureAmountChecker[_pairIndex].toString());
        console.log("decreasePosition longTracker", longTracker[_pairIndex], "shortTracker", shortTracker[_pairIndex]);


        IPairVault.Vault memory lpVault = pairVault.getVault(_pairIndex);
        if (prevNetExposureAmountChecker > 0) {

            if (netExposureAmountChecker[_pairIndex] > prevNetExposureAmountChecker) {
                console.log("decreasePosition STC long increase");
                pairVault.increaseReserveAmount(_pairIndex, _sizeAmount, 0);
                uint256 averagePrice = (uint256(prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) + sizeDelta)
                    .calculatePrice(uint256(prevNetExposureAmountChecker) + _sizeAmount);
                console.log("decreasePosition STC update averagePrice", averagePrice);
                pairVault.updateAveragePrice(_pairIndex, averagePrice);
            } else if (netExposureAmountChecker[_pairIndex] > 0) {

                console.log("decreasePosition BTC long decrease");
                pairVault.decreaseReserveAmount(_pairIndex, _sizeAmount, 0);

                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("decreasePosition BTC decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("decreasePosition BTC increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                }
            } else {
                console.log("decreasePosition BTC long to short");
                pairVault.decreaseReserveAmount(_pairIndex, lpVault.indexReservedAmount, 0);
                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("decreasePosition BTC decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("decreasePosition BTC increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                }
                pairVault.increaseReserveAmount(_pairIndex, 0, (_sizeAmount - uint256(prevNetExposureAmountChecker)).mulPrice(_price));
                console.log("decreasePosition BTC Long to Short update averagePrice", _price);
                pairVault.updateAveragePrice(_pairIndex, _price);
            }
        } else if (prevNetExposureAmountChecker < 0) {

            if (netExposureAmountChecker[_pairIndex] < prevNetExposureAmountChecker) {
                console.log("decreasePosition BTC short increase");
                pairVault.increaseReserveAmount(_pairIndex, 0, sizeDelta);

                uint256 averagePrice = (uint256(- prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) + sizeDelta)
                    .calculatePrice(uint256(- prevNetExposureAmountChecker) + _sizeAmount);
                console.log("decreasePosition BTC update averagePrice", averagePrice);
                pairVault.updateAveragePrice(_pairIndex, averagePrice);
            } else if (netExposureAmountChecker[_pairIndex] < 0) {

                console.log("decreasePosition STC short decrease");
                pairVault.decreaseReserveAmount(_pairIndex, 0, _sizeAmount.mulPrice(lpVault.averagePrice));

                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("decreasePosition STC decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("decreasePosition STC increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                }
            } else {
                console.log("decreasePosition STC short to long");
                pairVault.decreaseReserveAmount(_pairIndex, 0, lpVault.stableReservedAmount);
                if (_price > lpVault.averagePrice) {
                    uint256 profit = _sizeAmount.mulPrice(_price - lpVault.averagePrice);
                    console.log("decreasePosition STC increaseProfit", profit);
                    IERC20(pair.stableToken).safeTransfer(address(pairVault), profit);
                    pairVault.increaseProfit(_pairIndex, profit);
                } else {
                    uint256 profit = _sizeAmount.mulPrice(lpVault.averagePrice - _price);
                    console.log("decreasePosition STC decreaseProfit", profit);
                    pairVault.decreaseProfit(_pairIndex, profit, _price);
                }
                pairVault.increaseReserveAmount(_pairIndex, 0, (_sizeAmount - uint256(- prevNetExposureAmountChecker)).mulPrice(_price));
                console.log("decreasePosition STC Long to Short update averagePrice", _price);
                pairVault.updateAveragePrice(_pairIndex, _price);
            }
        } else {

            if (netExposureAmountChecker[_pairIndex] > 0) {
                console.log("decreasePosition STC zero to long");
                pairVault.increaseReserveAmount(_pairIndex, _sizeAmount, 0);
            } else {
                console.log("decreasePosition BTC zero to short");
                pairVault.increaseReserveAmount(_pairIndex, 0, sizeDelta);
            }
            pairVault.updateAveragePrice(_pairIndex, _price);
        }
        uint256 price = vaultPriceFeed.getPrice(pair.indexToken);
        pnl = position.getUnrealizedPnl(_sizeAmount, price);
        console.log("decreasePosition pnl", pnl.toString());

        if (pnl > 0) {
            transferOut += pnl.abs();
        } else {
            position.collateral -= position.collateral.min(uint256(- pnl));
        }
        position.realisedPnl += pnl;

        console.log("decreasePosition collateral", position.collateral);

        if (transferOut > 0) {
            IERC20(pair.stableToken).safeTransfer(_account, transferOut);
        }


        if (position.positionAmount == 0) {

            if (position.collateral > 0) {
                IERC20(pair.stableToken).safeTransfer(position.account, position.collateral);
            }

            console.log("decreasePosition position close");
            delete positions[positionKey];
            emit ClosePosition(
                positionKey,
                _account,
                _pairIndex,
                _isLong
            );
        }

        emit DecreasePosition(
            positionKey,
            _account,
            _pairIndex,
            _isLong,
            _collateral,
            _sizeAmount,
            _price,
            tradingFee,
            fundingFee,
            pnl,
            transferOut
        );

        emit UpdatePosition(
            positionKey,
            _account,
            _pairIndex,
            _isLong,
            position.collateral,
            position.positionAmount,
            position.averagePrice,
            position.entryFundingRate,
            position.entryFundingTime,
            position.realisedPnl,
            _price
        );
    }

    function updateCumulativeFundingRate(uint256 _pairIndex, uint256 _price) public {
        if (lastFundingTimes[_pairIndex] == 0) {
            lastFundingTimes[_pairIndex] = block.timestamp / fundingInterval * fundingInterval;
            return;
        }

        if (block.timestamp - lastFundingTimes[_pairIndex] < fundingInterval) {
            console.log("updateCumulativeFundingRate no need update");
            return;
        }

        uint256 intervals = (block.timestamp - lastFundingTimes[_pairIndex]) / fundingInterval;
        int256 nextFundingRate = _currentFundingRate(_pairIndex, _price);

        lastFundingRates[_pairIndex] = nextFundingRate;
        cumulativeFundingRates[_pairIndex] = cumulativeFundingRates[_pairIndex] + nextFundingRate * int256(intervals);
        lastFundingTimes[_pairIndex] = block.timestamp / fundingInterval * fundingInterval;

        emit UpdateFundingRate(_pairIndex, cumulativeFundingRates[_pairIndex], lastFundingTimes[_pairIndex]);
    }

    function buyIndexToken(uint256 _pairIndex, uint256 _amount) public onlyHandler {
        IPairInfo.Pair memory pair = pairInfo.getPair(_pairIndex);
        uint256 price = vaultPriceFeed.getPrice(pair.indexToken);
        uint256 stableAmount = _amount.mulPrice(price);


        IERC20(pair.stableToken).safeTransferFrom(msg.sender, address(this), stableAmount);
        IERC20(pair.indexToken).safeTransfer(msg.sender, _amount);
    }

    function getTradingFee(uint256 _pairIndex, bool _isLong, uint256 _sizeAmount) external override view returns (uint256 tradingFee) {
        IPairInfo.Pair memory pair = pairInfo.getPair(_pairIndex);
        uint256 price = vaultPriceFeed.getPrice(pair.indexToken);
        return _tradingFee(_pairIndex, _isLong, _sizeAmount, price);
    }

    function _tradingFee(uint256 _pairIndex, bool _isLong, uint256 _sizeAmount, uint256 _price) internal view returns (uint256 tradingFee) {
        uint256 sizeDelta = _sizeAmount.mulPrice(_price);

        IPairInfo.TradingFeeConfig memory tradingFeeConfig = pairInfo.getTradingFeeConfig(_pairIndex);
        if (netExposureAmountChecker[_pairIndex] >= 0) {

            if (_isLong) {
                // fee
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.takerFeeP);
            } else {
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.makerFeeP);
            }
        } else {

            if (_isLong) {
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.makerFeeP);
            } else {
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.takerFeeP);
            }
        }
        return tradingFee;
    }

    function getFundingFee(
        bool _increase,
        address _account,
        uint256 _pairIndex,
        bool _isLong,
        uint256 _sizeAmount
    ) public override view returns (int256) {
        Position.Info memory position = positions.get(_account, _pairIndex, _isLong);

        uint256 interval = block.timestamp - position.entryFundingTime;
        if (interval < fundingInterval) {
            if (!_increase) {
                int256 fundingRate = lastFundingRates[_pairIndex] * int256(interval) / int256(fundingInterval);
                return int256(_sizeAmount) * fundingRate / int256(PrecisionUtils.fundingRatePrecision());
            }
        }

        int256 fundingRate = cumulativeFundingRates[_pairIndex] - position.entryFundingRate;
        return int256(position.positionAmount) * fundingRate / int256(PrecisionUtils.fundingRatePrecision());
    }

    function getCurrentFundingRate(uint256 _pairIndex) external override view returns (int256) {
        IPairInfo.Pair memory pair = pairInfo.getPair(_pairIndex);
        uint256 price = vaultPriceFeed.getPrice(pair.indexToken);
        return _currentFundingRate(_pairIndex, price);
    }

    function _currentFundingRate(uint256 _pairIndex, uint256 _price) internal view returns (int256) {
        IPairInfo.FundingFeeConfig memory fundingFeeConfig = pairInfo.getFundingFeeConfig(_pairIndex);

        uint256 absNetExposure = netExposureAmountChecker[_pairIndex] >= 0 ? uint256(netExposureAmountChecker[_pairIndex]) : uint256(- netExposureAmountChecker[_pairIndex]);
        uint256 w = fundingFeeConfig.fundingWeightFactor;
        uint256 q = longTracker[_pairIndex] + shortTracker[_pairIndex];
        uint256 k = fundingFeeConfig.liquidityPremiumFactor;

        IPairVault.Vault memory lpVault = pairVault.getVault(_pairIndex);
        uint256 l = (lpVault.indexTotalAmount - lpVault.indexReservedAmount).mulPrice(_price) + (lpVault.stableTotalAmount - lpVault.stableReservedAmount);

        uint256 fundingRate = w * absNetExposure * PrecisionUtils.fundingRatePrecision() / (k * q)
            + (PrecisionUtils.fundingRatePrecision() - w) * absNetExposure / (k * l);

        fundingRate = fundingRate >= fundingFeeConfig.interest ?
            (fundingRate - fundingFeeConfig.interest).min(fundingFeeConfig.minFundingRate).max(fundingFeeConfig.maxFundingRate) :
            (fundingFeeConfig.interest - fundingRate).min(fundingFeeConfig.minFundingRate).max(fundingFeeConfig.maxFundingRate);
        console.log("getCurrentFundingRate fundingRate", fundingRate);

        return netExposureAmountChecker[_pairIndex] >= 0 ? int256(fundingRate) : - int256(fundingRate);
    }

    function getPosition(address _account, uint256 _pairIndex, bool _isLong) public view returns (Position.Info memory) {
        Position.Info memory position = positions.get(_account, _pairIndex, _isLong);
        return position;

    }

    function getPositionByKey(bytes32 key) public view returns (Position.Info memory) {
        Position.Info memory position = positions[key];
        return position;
    }

    function getPositionKey(address _account, uint256 _pairIndex, bool _isLong) public pure returns (bytes32) {
        return PositionKey.getPositionKey(_account, _pairIndex, _isLong);
    }

}

