contracts/trading/Executor.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/security/Pausable.sol';

import '../libraries/Position.sol';
import '../interfaces/IExecutor.sol';
import '../interfaces/IAddressesProvider.sol';
import '../interfaces/IRoleManager.sol';
import '../interfaces/IOrderManager.sol';
import '../interfaces/IPositionManager.sol';
import '../interfaces/IIndexPriceFeed.sol';
import '../interfaces/IPool.sol';
import '../interfaces/IPool.sol';
import '../interfaces/IPositionManager.sol';
import '../interfaces/IOraclePriceFeed.sol';

contract Executor is IExecutor, Pausable {
    using SafeERC20 for IERC20;
    using PrecisionUtils for uint256;
    using Math for uint256;
    using Int256Utils for int256;
    using Position for Position.Info;

    uint256 public override increaseMarketOrderStartIndex;
    uint256 public override decreaseMarketOrderStartIndex;

    uint256 public override maxTimeDelay;

    IAddressesProvider public immutable ADDRESS_PROVIDER;

    IOrderManager public orderManager;
    IPool public pool;
    IPositionManager public positionManager;

    constructor(
        IAddressesProvider addressProvider,
        IPool _pool,
        IOrderManager _orderManager,
        IPositionManager _tradingVault,
        uint256 _maxTimeDelay
    ) {
        ADDRESS_PROVIDER = addressProvider;
        pool = _pool;
        orderManager = _orderManager;
        positionManager = _tradingVault;
        maxTimeDelay = _maxTimeDelay;
    }

    modifier onlyAdmin() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isAdmin(msg.sender), 'onlyAdmin');
        _;
    }

    modifier onlyPoolAdmin() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isPoolAdmin(msg.sender), 'onlyPoolAdmin');
        _;
    }

    modifier onlyPositionKeeper() {
        require(IRoleManager(ADDRESS_PROVIDER.getRoleManager()).isKeeper(msg.sender), 'onlyPositionKeeper');
        _;
    }

    function updateMaxTimeDelay(uint256 newMaxTimeDelay) external override whenNotPaused onlyPoolAdmin {
        uint256 oldDelay = maxTimeDelay;
        maxTimeDelay = newMaxTimeDelay;
        emit UpdateMaxTimeDelay(oldDelay, newMaxTimeDelay);
    }

    function setPricesAndExecuteMarketOrders(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        uint256 increaseEndIndex,
        uint256 decreaseEndIndex
    ) external onlyPositionKeeper whenNotPaused {
        require(tokens.length == prices.length && tokens.length >= 0, 'invalid params');

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        this.executeIncreaseMarketOrders(increaseEndIndex);
        this.executeDecreaseMarketOrders(decreaseEndIndex);
    }

    function setPricesAndExecuteLimitOrders(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        uint256[] memory increaseOrderIds,
        uint256[] memory decreaseOrderIds
    ) external onlyPositionKeeper whenNotPaused {
        require(tokens.length == prices.length && tokens.length >= 0, 'invalid params');

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        this.executeIncreaseLimitOrders(increaseOrderIds);
        this.executeDecreaseLimitOrders(decreaseOrderIds);
    }

    function executeIncreaseMarketOrders(uint256 endIndex) external onlyPositionKeeper whenNotPaused {
        uint256 index = increaseMarketOrderStartIndex;
        uint256 length = orderManager.increaseMarketOrdersIndex();

        if (index >= length) {
            return;
        }
        if (endIndex > length) {
            endIndex = length;
        }

        while (index < endIndex) {
            try this.executeIncreaseOrder(index, TradingTypes.TradeType.MARKET) {} catch Error(string memory reason) {
                orderManager.cancelOrder(index, TradingTypes.TradeType.MARKET, true);
            }
            index++;
        }
    }

    function executeIncreaseLimitOrders(uint256[] memory orderIds) external onlyPositionKeeper whenNotPaused {
        for (uint256 i = 0; i < orderIds.length; i++) {
            try this.executeIncreaseOrder(orderIds[i], TradingTypes.TradeType.LIMIT) {} catch Error(
                string memory reason
            ) {}
        }
    }

    function executeIncreaseOrder(
        uint256 _orderId,
        TradingTypes.TradeType _tradeType
    ) external onlyPositionKeeper whenNotPaused {
        TradingTypes.IncreasePositionOrder memory order = orderManager.getIncreaseOrder(_orderId, _tradeType);

        if (order.account == address(0)) {
            return;
        }

        // expire
        if (_tradeType == TradingTypes.TradeType.MARKET) {
            require(order.blockTime + maxTimeDelay >= block.timestamp, 'order expired');
        }

        // check pair enable
        uint256 pairIndex = order.pairIndex;
        IPool.Pair memory pair = pool.getPair(pairIndex);
        require(pair.enable, 'trade pair not supported');

        // check account enable
        require(!positionManager.isFrozen(order.account), 'account is frozen');

        // check trading amount
        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(pairIndex);
        require(
            order.sizeAmount == 0 ||
                (order.sizeAmount >= tradingConfig.minTradeAmount && order.sizeAmount <= tradingConfig.maxTradeAmount),
            'invalid trade size'
        );

        // check price
        // IPool.Pair memory pair = pool.getPair(pairIndex);
        uint256 price = positionManager.getValidPrice(pair.indexToken, pairIndex, order.isLong);
        if (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT) {
            require(
                order.isLong
                    ? price.mulPercentage(PrecisionUtils.percentage() - tradingConfig.priceSlipP) <=
                        order.openPrice
                    : price.mulPercentage(PrecisionUtils.percentage() + tradingConfig.priceSlipP) >=
                        order.openPrice,
                'not reach trigger price'
            );
        } else {
            require(order.isLong ? price >= order.openPrice : price <= order.openPrice, 'not reach trigger price');
        }

        // compare openPrice and oraclePrice
        if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            if (order.isLong) {
                price = order.openPrice.min(price);
            } else {
                price = order.openPrice.max(price);
            }
        }

        // get position
        Position.Info memory position = positionManager.getPosition(order.account, order.pairIndex, order.isLong);

        uint256 sizeDelta = order.sizeAmount.mulPrice(price);

        // check position and leverage
        (uint256 afterPosition, ) = position.validLeverage(
            price,
            order.collateral,
            order.sizeAmount,
            true,
            tradingConfig.minLeverage,
            tradingConfig.maxLeverage,
            tradingConfig.maxPositionAmount
        );
        require(afterPosition > 0, 'zero position amount');

        IPool.Vault memory lpVault = pool.getVault(pairIndex);

        int256 preNetExposureAmountChecker = positionManager.netExposureAmountChecker(order.pairIndex);
        if (preNetExposureAmountChecker >= 0) {
            if (order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                require(order.sizeAmount <= availableIndex, 'lp index token not enough');
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                require(
                    order.sizeAmount <= uint256(preNetExposureAmountChecker) + availableStable.divPrice(price),
                    'lp stable token not enough'
                );
            }
        } else {
            if (order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                require(
                    order.sizeAmount <= uint256(-preNetExposureAmountChecker) + availableIndex,
                    'lp index token not enough'
                );
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                require(order.sizeAmount <= availableStable.divPrice(price), 'lp stable token not enough');
            }
        }

        // transfer collateral
        if (order.collateral > 0) {
            positionManager.transferTokenTo(pair.stableToken, address(positionManager), order.collateral.abs());
        }
        (uint256 tradingFee, int256 fundingFee) = positionManager.increasePosition(
            order.account,
            pairIndex,
            order.collateral,
            order.sizeAmount,
            order.isLong,
            price
        );

        orderManager.removeOrderFromPosition(
            IOrderManager.PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                true,
                order.tradeType,
                _orderId,
                order.sizeAmount
            )
        );

        bytes32 orderKey = PositionKey.getOrderKey(true, order.tradeType, _orderId);
        TradingTypes.OrderWithTpSl memory orderTpSl = orderManager.getOrderTpSl(orderKey);
        if (orderTpSl.tp > 0) {
            orderManager.createOrder(
                TradingTypes.CreateOrderRequest({
                    account: order.account,
                    pairIndex: order.pairIndex,
                    tradeType: TradingTypes.TradeType.TP,
                    collateral: 0,
                    openPrice: orderTpSl.tpPrice,
                    isLong: order.isLong,
                    sizeAmount: -int256(orderTpSl.tp)
                })
            );
        }
        if (orderTpSl.sl > 0) {
            orderManager.createOrder(
                TradingTypes.CreateOrderRequest({
                    account: order.account,
                    pairIndex: order.pairIndex,
                    tradeType: TradingTypes.TradeType.SL,
                    collateral: 0,
                    openPrice: orderTpSl.slPrice,
                    isLong: order.isLong,
                    sizeAmount: -int256(orderTpSl.sl)
                })
            );
        }

        orderManager.removeOrderTpSl(orderKey);

        // delete order
        if (_tradeType == TradingTypes.TradeType.MARKET) {
            orderManager.removeIncreaseMarketOrders(_orderId);
        } else if (_tradeType == TradingTypes.TradeType.LIMIT) {
            orderManager.removeIncreaseLimitOrders(_orderId);
        }

        emit ExecuteIncreaseOrder(
            order.account,
            _orderId,
            pairIndex,
            _tradeType,
            order.collateral,
            order.isLong,
            order.sizeAmount,
            price,
            tradingFee,
            fundingFee
        );
    }

    function executeDecreaseMarketOrders(uint256 endIndex) external onlyPositionKeeper whenNotPaused {
        uint256 index = decreaseMarketOrderStartIndex;
        uint256 length = orderManager.decreaseMarketOrdersIndex();
        if (index >= length) {
            return;
        }
        if (endIndex > length) {
            endIndex = length;
        }

        while (index < endIndex) {
            try this.executeDecreaseOrder(index, TradingTypes.TradeType.MARKET) {} catch Error(string memory reason) {
                orderManager.cancelOrder(index, TradingTypes.TradeType.MARKET, false);
            }
            index++;
        }
    }

    function executeDecreaseLimitOrders(uint256[] memory orderIds) external onlyPositionKeeper whenNotPaused {
        for (uint256 i = 0; i < orderIds.length; i++) {
            try this.executeDecreaseOrder(orderIds[i], TradingTypes.TradeType.LIMIT) {} catch Error(
                string memory reason
            ) {}
        }
    }

    function executeDecreaseOrder(
        uint256 _orderId,
        TradingTypes.TradeType _tradeType
    ) external onlyPositionKeeper whenNotPaused {
        _executeDecreaseOrder(_orderId, _tradeType);
    }

    function _executeDecreaseOrder(uint256 _orderId, TradingTypes.TradeType _tradeType) internal {
        TradingTypes.DecreasePositionOrder memory order = orderManager.getDecreaseOrder(_orderId, _tradeType);

        if (order.account == address(0)) {
            return;
        }

        // expire
        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            require(order.blockTime + maxTimeDelay >= block.timestamp, 'order expired');
        }

        // get pair
        uint256 pairIndex = order.pairIndex;
        IPool.Pair memory pair = pool.getPair(pairIndex);

        // get position
        Position.Info memory position = positionManager.getPosition(order.account, order.pairIndex, order.isLong);
        if (position.positionAmount == 0) {
            return;
        }

        // check trading amount
        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(pairIndex);

        order.sizeAmount = order.sizeAmount.min(position.positionAmount);
        require(
            order.sizeAmount == 0 ||
                (order.sizeAmount >= tradingConfig.minTradeAmount && order.sizeAmount <= tradingConfig.maxTradeAmount),
            'invalid trade size'
        );
        // IPool.Pair memory pair = pool.getPair(pairIndex);
        // check price
        uint256 price = positionManager.getValidPrice(pair.indexToken, pairIndex, order.isLong);
        if (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT) {
            require(
                order.abovePrice
                    ? price.mulPercentage(PrecisionUtils.percentage() - tradingConfig.priceSlipP) <=
                        order.triggerPrice
                    : price.mulPercentage(PrecisionUtils.percentage() + tradingConfig.priceSlipP) >=
                        order.triggerPrice,
                'not reach trigger price'
            );
        } else {
            require(
                order.abovePrice ? price <= order.triggerPrice : price >= order.triggerPrice,
                'not reach trigger price'
            );
        }

        // compare openPrice and oraclePrice
        if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            if (!order.isLong) {
                price = order.triggerPrice.min(price);
            } else {
                price = order.triggerPrice.max(price);
            }
        }

        uint256 sizeDelta = order.sizeAmount.mulPrice(price);

        // check position and leverage
        position.validLeverage(
            price,
            order.collateral,
            order.sizeAmount,
            false,
            tradingConfig.minLeverage,
            tradingConfig.maxLeverage,
            tradingConfig.maxPositionAmount
        );

        IPool.Vault memory lpVault = pool.getVault(pairIndex);

        int256 preNetExposureAmountChecker = positionManager.netExposureAmountChecker(order.pairIndex);
        bool needADL;
        if (preNetExposureAmountChecker >= 0) {
            if (!order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                needADL = order.sizeAmount > availableIndex;
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                needADL = order.sizeAmount > uint256(preNetExposureAmountChecker) + availableStable.divPrice(price);
            }
        } else {
            if (!order.isLong) {
                uint256 availableIndex = lpVault.indexTotalAmount - lpVault.indexReservedAmount;
                needADL = order.sizeAmount > uint256(-preNetExposureAmountChecker) + availableIndex;
            } else {
                uint256 availableStable = lpVault.stableTotalAmount - lpVault.stableReservedAmount;
                needADL = order.sizeAmount > availableStable.divPrice(price);
            }
        }

        if (needADL) {
            orderManager.setOrderNeedADL(_orderId, order.tradeType, needADL);

            emit ExecuteDecreaseOrder(
                order.account,
                _orderId,
                pairIndex,
                order.tradeType,
                order.isLong,
                order.sizeAmount,
                price,
                0,
                needADL,
                0,
                0
            );
            return;
        }

        // transfer collateral
        if (order.collateral > 0) {
            IPool.Pair memory pair = pool.getPair(position.pairIndex);
            positionManager.transferTokenTo(pair.stableToken, address(pool), order.collateral.abs());
        }
        (uint256 tradingFee, int256 fundingFee, int256 pnl) = positionManager.decreasePosition(
            order.account,
            pairIndex,
            order.collateral,
            order.sizeAmount,
            order.isLong,
            price
        );

        // delete order
        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            orderManager.removeDecreaseMarketOrders(_orderId);
        } else if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            orderManager.removeDecreaseLimitOrders(_orderId);
        } else {
            orderManager.setPositionHasTpSl(position.key, order.tradeType, false);
            orderManager.removeDecreaseLimitOrders(_orderId);
        }

        // remove decrease order
        orderManager.removeOrderFromPosition(
            IOrderManager.PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                false,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            )
        );

        position = positionManager.getPosition(order.account, order.pairIndex, order.isLong);

        if (position.positionAmount == 0) {
            // cancel all decrease order
            bytes32 key = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);
            IOrderManager.PositionOrder[] memory orders = orderManager.getPositionOrders(key);

            for (uint256 i = 0; i < orders.length; i++) {
                IOrderManager.PositionOrder memory positionOrder = orders[i];
                if (!positionOrder.isIncrease) {
                    orderManager.cancelOrder(positionOrder.orderId, positionOrder.tradeType, false);
                }
            }
        }

        emit ExecuteDecreaseOrder(
            order.account,
            _orderId,
            pairIndex,
            order.tradeType,
            order.isLong,
            order.sizeAmount,
            price,
            pnl,
            needADL,
            tradingFee,
            fundingFee
        );
    }

    function setPricesAndLiquidatePositions(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        bytes32[] memory positionKeys
    ) external onlyPositionKeeper whenNotPaused {
        require(tokens.length == prices.length && tokens.length >= 0, 'invalid params');

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        this.liquidatePositions(positionKeys);
    }

    function liquidatePositions(bytes32[] memory positionKeys) external onlyPositionKeeper whenNotPaused {
        for (uint256 i = 0; i < positionKeys.length; i++) {
            _liquidatePosition(positionKeys[i]);
        }
    }

    function setPricesAndExecuteADL(
        address[] memory tokens,
        uint256[] memory prices,
        uint256 timestamp,
        bytes32[] memory positionKeys,
        uint256[] memory sizeAmounts,
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) external onlyPositionKeeper whenNotPaused {
        require(tokens.length == prices.length && tokens.length >= 0, 'invalid params');

        IIndexPriceFeed(ADDRESS_PROVIDER.getIndexPriceOracle()).setPrices(tokens, prices, timestamp);

        this.executeADLAndDecreaseOrder(positionKeys, sizeAmounts, orderId, tradeType);
    }

    function executeADLAndDecreaseOrder(
        bytes32[] memory _positionKeys,
        uint256[] memory _sizeAmounts,
        uint256 _orderId,
        TradingTypes.TradeType _tradeType
    ) public onlyPositionKeeper whenNotPaused {
        require(_positionKeys.length == _sizeAmounts.length, 'length not match');

        TradingTypes.DecreasePositionOrder memory order = orderManager.getDecreaseOrder(_orderId, _tradeType);
        require(order.needADL, 'no need ADL');

        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(order.pairIndex);

        Position.Info[] memory adlPositions = new Position.Info[](_positionKeys.length);
        uint256 sumAmount;
        for (uint256 i = 0; i < _positionKeys.length; i++) {
            Position.Info memory position = positionManager.getPositionByKey(_positionKeys[i]);
            require(_sizeAmounts[i] <= position.positionAmount, 'ADL size exceeds position');
            require(_sizeAmounts[i] <= tradingConfig.maxTradeAmount, 'exceeds max trade amount');
            sumAmount += _sizeAmounts[i];
            adlPositions[i] = position;
        }

        require(sumAmount == order.sizeAmount, 'ADL position amount not match decrease order');
        IPool.Pair memory pair = pool.getPair(order.pairIndex);
        uint256 price = positionManager.getValidPrice(pair.indexToken, order.pairIndex, !order.isLong);

        for (uint256 i = 0; i < adlPositions.length; i++) {
            Position.Info memory adlPosition = adlPositions[i];
            uint256 orderId = orderManager.createOrder(
                TradingTypes.CreateOrderRequest({
                    account: adlPosition.account,
                    pairIndex: adlPosition.pairIndex,
                    tradeType: TradingTypes.TradeType.MARKET,
                    collateral: 0,
                    openPrice: price,
                    isLong: adlPosition.isLong,
                    sizeAmount: -int256(adlPosition.positionAmount)
                })
            );
            _executeDecreaseOrder(orderId, TradingTypes.TradeType.MARKET);
        }
        _executeDecreaseOrder(_orderId, order.tradeType);
    }

    function _liquidatePosition(bytes32 _positionKey) internal {
        Position.Info memory position = positionManager.getPositionByKey(_positionKey);

        if (position.positionAmount == 0) {
            return;
        }
        IPool.Pair memory pair = pool.getPair(position.pairIndex);
        uint256 price = positionManager.getValidPrice(pair.indexToken, position.pairIndex, position.isLong);

        int256 unrealizedPnl = position.getUnrealizedPnl(position.positionAmount, price);
        uint256 tradingFee = positionManager.getTradingFee(position.pairIndex, position.isLong, position.positionAmount);
        int256 fundingFee = positionManager.getFundingFee(false, position.account, position.pairIndex, position.isLong, position.positionAmount);
        int256 exposureAsset = int256(position.collateral) + unrealizedPnl - int256(tradingFee) + (position.isLong ? -fundingFee : fundingFee);

        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(position.pairIndex);

        bool needLiquidate;
        if (exposureAsset <= 0) {
            needLiquidate = true;
        } else {
            uint256 riskRate = position
                .positionAmount
                .mulPrice(position.averagePrice)
                .mulPercentage(tradingConfig.maintainMarginRate)
                .calculatePercentage(uint256(exposureAsset));
            needLiquidate = riskRate >= PrecisionUtils.percentage();
        }
        if (!needLiquidate) {
            return;
        }

        // cancel all positionOrders
        orderManager.cancelAllPositionOrders(position.account, position.pairIndex, position.isLong);

        uint256 orderId = orderManager.createOrder(
            TradingTypes.CreateOrderRequest({
                account: position.account,
                pairIndex: position.pairIndex,
                tradeType: TradingTypes.TradeType.MARKET,
                collateral: 0,
                openPrice: price,
                isLong: position.isLong,
                sizeAmount: -int256(position.positionAmount)
            })
        );

        _executeDecreaseOrder(orderId, TradingTypes.TradeType.MARKET);

        emit LiquidatePosition(
            _positionKey,
            position.account,
            position.pairIndex,
            position.isLong,
            position.positionAmount,
            position.collateral,
            price,
            orderId
        );
    }

    function setPaused() external onlyAdmin {
        _pause();
    }

    function setUnPaused() external onlyAdmin {
        _unpause();
    }
}

contracts/trading/OrderManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/security/Pausable.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/utils/math/Math.sol';

import '../interfaces/IOraclePriceFeed.sol';
import '../interfaces/IPool.sol';
import '../interfaces/IPool.sol';
import '../libraries/PrecisionUtils.sol';
import '../libraries/PositionKey.sol';
import '../libraries/Int256Utils.sol';
import '../libraries/Roleable.sol';
import '../libraries/TradingTypes.sol';

import 'hardhat/console.sol';
import '../interfaces/IOrderManager.sol';
import '../interfaces/IAddressesProvider.sol';
import '../interfaces/IRoleManager.sol';
import '../interfaces/IPositionManager.sol';

contract OrderManager is IOrderManager, ReentrancyGuard, Roleable, Pausable {
    using SafeERC20 for IERC20;
    using PrecisionUtils for uint256;
    using Math for uint256;
    using Int256Utils for int256;
    using Position for mapping(bytes32 => Position.Info);
    using Position for Position.Info;

    mapping(uint256 => TradingTypes.IncreasePositionOrder) public increaseMarketOrders;
    uint256 public override increaseMarketOrdersIndex;

    mapping(uint256 => TradingTypes.DecreasePositionOrder) public decreaseMarketOrders;
    uint256 public override decreaseMarketOrdersIndex;

    mapping(uint256 => TradingTypes.IncreasePositionOrder) public increaseLimitOrders;
    uint256 public override increaseLimitOrdersIndex;

    mapping(uint256 => TradingTypes.DecreasePositionOrder) public decreaseLimitOrders;
    uint256 public override decreaseLimitOrdersIndex;

    mapping(bytes32 => TradingTypes.OrderWithTpSl) public orderWithTpSl; // OrderKey -> TpSl

    mapping(bytes32 => PositionOrder[]) public positionOrders;
    mapping(bytes32 => mapping(bytes32 => uint256)) public positionOrderIndex;

    mapping(bytes32 => uint256) public positionDecreaseTotalAmount;

    mapping(bytes32 => mapping(TradingTypes.TradeType => bool)) public positionHasTpSl; // PositionKey -> TradeType -> bool

    IPool public pool;
    IPositionManager public tradingVault;
    IPositionManager public positionManager;
    address public addressExecutor;
    address public router;

    constructor(
        IAddressesProvider addressProvider,
        IPool _pairInfo,
        IPositionManager _tradingVault
    ) Roleable(addressProvider) {
        pool = _pairInfo;
        tradingVault = _tradingVault;
    }

    modifier onlyRouter() {
        require(msg.sender == router, 'onlyRouter');
        _;
    }

    modifier onlyExecutor() {
        require(msg.sender == addressExecutor, 'onlyExecutor');
        _;
    }

    modifier onlyCreateOrderAddress(address account) {
        require(msg.sender == router || msg.sender == addressExecutor || account == msg.sender, 'no access');
        _;
    }

    modifier onlyExecutorOrAccount(address account) {
        require(msg.sender == address(addressExecutor) || account == msg.sender, 'no access');
        _;
    }

    function setExecutor(address _addressExecutor) external onlyPoolAdmin {
        addressExecutor = _addressExecutor;
    }

    function setRouter(address _router) external onlyPoolAdmin {
        router = _router;
    }

    function updatePositionManager(address newAddress) external onlyPoolAdmin {
        address oldAddress = address(positionManager);
        positionManager = IPositionManager(newAddress);
        emit UpdatePositionManager(oldAddress, newAddress);
    }

    function getOrderTpSl(bytes32 orderKey) public view override returns (TradingTypes.OrderWithTpSl memory) {
        return orderWithTpSl[orderKey];
    }

    function getPositionOrders(bytes32 key) public view override returns (PositionOrder[] memory) {
        return positionOrders[key];
    }

    function createOrder(
        TradingTypes.CreateOrderRequest memory request
    ) public nonReentrant onlyCreateOrderAddress(request.account) whenNotPaused returns (uint256 orderId) {
        require(address(positionManager) != address(0), 'zero address');

        address account = request.account;
        require(!tradingVault.isFrozen(account), 'account is frozen');

        IPool.Pair memory pair = pool.getPair(request.pairIndex);
        require(pair.enable, 'trade pair not supported');

        if (request.tradeType == TradingTypes.TradeType.MARKET || request.tradeType == TradingTypes.TradeType.LIMIT) {
            // check size
            require(
                request.sizeAmount == 0 || _checkTradingAmount(request.pairIndex, request.sizeAmount.abs()),
                'invalid trade size'
            );

            bytes32 positionKey = PositionKey.getPositionKey(account, request.pairIndex, request.isLong);
            Position.Info memory position = tradingVault.getPosition(account, request.pairIndex, request.isLong);
            uint256 price = IOraclePriceFeed(ADDRESS_PROVIDER.getPriceOracle()).getPrice(pair.indexToken);
            IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(position.pairIndex);
            //TODO if size = 0
            if (request.sizeAmount >= 0) {
                // check leverage
                (uint256 afterPosition,) = position.validLeverage(
                    price,
                    request.collateral,
                    uint256(request.sizeAmount),
                    true,
                    tradingConfig.minLeverage,
                    tradingConfig.maxLeverage,
                    tradingConfig.maxPositionAmount
                );
                // (uint256 afterPosition,) = tradingUtils.validLeverage(account, request.pairIndex, request.isLong, request.collateral, uint256(request.sizeAmount), true);
                require(afterPosition > 0, 'zero position amount');
            }
            if (request.sizeAmount <= 0) {
                // check leverage
                position.validLeverage(
                    price,
                    request.collateral,
                    uint256(request.sizeAmount.abs()),
                    false,
                    tradingConfig.minLeverage,
                    tradingConfig.maxLeverage,
                    tradingConfig.maxPositionAmount
                );

                //TODO if request size exceed position size, can calculate the max size
                require(
                    uint256(request.sizeAmount.abs()) <=
                    position.positionAmount - positionDecreaseTotalAmount[positionKey],
                    'decrease amount exceed position'
                );
            }

            // transfer collateral
            if (request.collateral > 0) {
                IERC20(pair.stableToken).safeTransferFrom(account, address(positionManager), request.collateral.abs());
            }
        }

        if (request.tradeType == TradingTypes.TradeType.TP || request.tradeType == TradingTypes.TradeType.SL) {
            Position.Info memory position = tradingVault.getPosition(account, request.pairIndex, request.isLong);
            require(
                uint256(request.sizeAmount.abs()) <= position.positionAmount,
                'tp/sl exceeds max size'
            );
            require(request.collateral == 0, 'no collateral required');
        }

        if (request.sizeAmount > 0) {
            return _createIncreaseOrder(
                TradingTypes.IncreasePositionRequest({
                    account: account,
                    pairIndex: request.pairIndex,
                    tradeType: request.tradeType,
                    collateral: request.collateral,
                    openPrice: request.openPrice,
                    isLong: request.isLong,
                    sizeAmount: uint256(request.sizeAmount)
                })
            );
        } else if (request.sizeAmount < 0) {
            return _createDecreaseOrder(
                TradingTypes.DecreasePositionRequest({
                    account: account,
                    pairIndex: request.pairIndex,
                    tradeType: request.tradeType,
                    collateral: request.collateral,
                    triggerPrice: request.openPrice,
                    sizeAmount: uint256(request.sizeAmount.abs()),
                    isLong: request.isLong
                })
            );
        } else {
            require(request.collateral != 0, 'not support');
            return _createIncreaseOrder(
                TradingTypes.IncreasePositionRequest({
                    account: account,
                    pairIndex: request.pairIndex,
                    tradeType: request.tradeType,
                    collateral: request.collateral,
                    openPrice: request.openPrice,
                    isLong: request.isLong,
                    sizeAmount: 0
                })
            );
        }
    }

    function cancelOrder(
        uint256 orderId,
        TradingTypes.TradeType tradeType,
        bool isIncrease
    ) public nonReentrant onlyCreateOrderAddress(msg.sender) whenNotPaused {
        if (isIncrease) {
            TradingTypes.IncreasePositionOrder memory order = getIncreaseOrder(orderId, tradeType);
            if (order.account == address(0)) {
                return;
            }

            _cancelIncreaseOrder(order);
        } else {
            TradingTypes.DecreasePositionOrder memory order = getDecreaseOrder(orderId, tradeType);
            if (order.account == address(0)) {
                return;
            }
            _cancelDecreaseOrder(order);
        }
    }

    function cancelAllPositionOrders(
        address account,
        uint256 pairIndex,
        bool isLong
    ) external onlyExecutorOrAccount(account) whenNotPaused {
        bytes32 key = PositionKey.getPositionKey(account, pairIndex, isLong);

        while (positionOrders[key].length > 0) {
            uint256 lastIndex = positionOrders[key].length - 1;
            PositionOrder memory positionOrder = positionOrders[key][lastIndex];

            this.cancelOrder(positionOrder.orderId, positionOrder.tradeType, positionOrder.isIncrease);
        }
    }

    function _checkTradingAmount(uint256 pairIndex, uint256 size) internal returns (bool) {
        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(pairIndex);
        return size >= tradingConfig.minTradeAmount && size <= tradingConfig.maxTradeAmount;
    }

    function _createIncreaseOrder(TradingTypes.IncreasePositionRequest memory _request) internal returns (uint256) {
        TradingTypes.IncreasePositionOrder memory order = TradingTypes.IncreasePositionOrder(
            0,
            _request.account,
            _request.pairIndex,
            _request.tradeType,
            _request.collateral,
            _request.openPrice,
            _request.isLong,
            _request.sizeAmount,
            block.timestamp
        );

        if (_request.tradeType == TradingTypes.TradeType.MARKET) {
            order.orderId = increaseMarketOrdersIndex;

            increaseMarketOrders[increaseMarketOrdersIndex++] = order;
        } else if (_request.tradeType == TradingTypes.TradeType.LIMIT) {
            order.orderId = increaseLimitOrdersIndex;

            increaseLimitOrders[increaseLimitOrdersIndex++] = order;
        } else {
            revert('invalid trade type');
        }

        this.addOrderToPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                true,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            )
        );

        //TODO remove tp sl from event
        emit CreateIncreaseOrder(
            order.account,
            order.orderId,
            _request.pairIndex,
            _request.tradeType,
            _request.collateral,
            _request.openPrice,
            _request.isLong,
            _request.sizeAmount,
            0,
            0,
            0,
            0
        );
        return order.orderId;
    }

    function _createDecreaseOrder(TradingTypes.DecreasePositionRequest memory _request) internal returns (uint256) {
        TradingTypes.DecreasePositionOrder memory order = TradingTypes.DecreasePositionOrder(
            0, // orderId
            _request.account,
            _request.pairIndex,
            _request.tradeType,
            _request.collateral,
            _request.triggerPrice,
            _request.sizeAmount,
            _request.isLong,
            false, // abovePrice
            block.timestamp,
            false
        );

        // abovePrice
        // marketlong: true,  short: false
        //  limitlong: false, short: true
        //     tplong: false, short: true
        //     sllong: true,  short: false
        if (_request.tradeType == TradingTypes.TradeType.MARKET) {
            order.orderId = decreaseMarketOrdersIndex;
            order.abovePrice = _request.isLong;

            decreaseMarketOrders[decreaseMarketOrdersIndex++] = order;
        } else if (_request.tradeType == TradingTypes.TradeType.LIMIT) {
            order.orderId = decreaseLimitOrdersIndex;
            order.abovePrice = !_request.isLong;

            decreaseLimitOrders[decreaseLimitOrdersIndex++] = order;
        } else if (_request.tradeType == TradingTypes.TradeType.TP) {
            order.orderId = decreaseLimitOrdersIndex;
            order.abovePrice = !_request.isLong;

            decreaseLimitOrders[decreaseLimitOrdersIndex++] = order;

            bytes32 positionKey = PositionKey.getPositionKey(_request.account, _request.pairIndex, _request.isLong);
            positionHasTpSl[positionKey][TradingTypes.TradeType.TP] = true;
        } else if (_request.tradeType == TradingTypes.TradeType.SL) {
            order.orderId = decreaseLimitOrdersIndex;
            order.abovePrice = _request.isLong;

            decreaseLimitOrders[decreaseLimitOrdersIndex++] = order;

            bytes32 positionKey = PositionKey.getPositionKey(_request.account, _request.pairIndex, _request.isLong);
            positionHasTpSl[positionKey][TradingTypes.TradeType.SL] = true;
        } else {
            revert('invalid trade type');
        }

        // add decrease order
        this.addOrderToPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                false,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            )
        );

        emit CreateDecreaseOrder(
            order.account,
            order.orderId,
            _request.tradeType,
            _request.collateral,
            _request.pairIndex,
            _request.triggerPrice,
            _request.sizeAmount,
            _request.isLong,
            order.abovePrice
        );
        return order.orderId;
    }

    function _cancelIncreaseOrder(TradingTypes.IncreasePositionOrder memory order) internal {
        IPool.Pair memory pair = pool.getPair(order.pairIndex);

        if (order.collateral > 0) {
            positionManager.transferTokenTo(pair.stableToken, order.account, order.collateral.abs());
        }

        this.removeOrderFromPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                true,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            )
        );

        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            delete increaseMarketOrders[order.orderId];
        } else if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            delete increaseLimitOrders[order.orderId];
        }

        emit CancelIncreaseOrder(order.account, order.orderId, order.tradeType);
    }

    function _cancelDecreaseOrder(TradingTypes.DecreasePositionOrder memory order) internal {
        IPool.Pair memory pair = pool.getPair(order.pairIndex);

        if (order.collateral > 0) {
            positionManager.transferTokenTo(pair.stableToken, order.account, order.collateral.abs());
        }

        this.removeOrderFromPosition(
            PositionOrder(
                order.account,
                order.pairIndex,
                order.isLong,
                false,
                order.tradeType,
                order.orderId,
                order.sizeAmount
            )
        );

        bytes32 key = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);

        if (order.tradeType == TradingTypes.TradeType.MARKET) {
            delete decreaseMarketOrders[order.orderId];
        } else if (order.tradeType == TradingTypes.TradeType.LIMIT) {
            delete decreaseLimitOrders[order.orderId];
        } else {
            positionHasTpSl[key][order.tradeType] = false;
            delete decreaseLimitOrders[order.orderId];
        }

        emit CancelDecreaseOrder(order.account, order.orderId, order.tradeType);
    }

    function getIncreaseOrder(
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) public view returns (TradingTypes.IncreasePositionOrder memory order) {
        if (tradeType == TradingTypes.TradeType.MARKET) {
            order = increaseMarketOrders[orderId];
        } else if (tradeType == TradingTypes.TradeType.LIMIT) {
            order = increaseLimitOrders[orderId];
        } else {
            revert('invalid trade type');
        }
        return order;
    }

    function getDecreaseOrder(
        uint256 orderId,
        TradingTypes.TradeType tradeType
    ) public view returns (TradingTypes.DecreasePositionOrder memory order) {
        if (tradeType == TradingTypes.TradeType.MARKET) {
            order = decreaseMarketOrders[orderId];
        } else {
            order = decreaseLimitOrders[orderId];
        }
        return order;
    }

    function addOrderToPosition(PositionOrder memory order) public onlyCreateOrderAddress(msg.sender) whenNotPaused {
        bytes32 positionKey = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);
        bytes32 orderKey = PositionKey.getOrderKey(order.isIncrease, order.tradeType, order.orderId);
        positionOrderIndex[positionKey][orderKey] = positionOrders[positionKey].length;
        positionOrders[positionKey].push(order);

        if (
            !order.isIncrease &&
            (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT)
        ) {
            positionDecreaseTotalAmount[positionKey] += order.sizeAmount;
        }
    }

    function removeOrderFromPosition(
        PositionOrder memory order
    ) public onlyCreateOrderAddress(msg.sender) whenNotPaused {
        bytes32 positionKey = PositionKey.getPositionKey(order.account, order.pairIndex, order.isLong);
        bytes32 orderKey = PositionKey.getOrderKey(order.isIncrease, order.tradeType, order.orderId);

        uint256 index = positionOrderIndex[positionKey][orderKey];
        uint256 lastIndex = positionOrders[positionKey].length - 1;

        if (index < lastIndex) {
            // swap last order
            PositionOrder memory lastOrder = positionOrders[positionKey][positionOrders[positionKey].length - 1];
            bytes32 lastOrderKey = PositionKey.getOrderKey(
                lastOrder.isIncrease,
                lastOrder.tradeType,
                lastOrder.orderId
            );

            positionOrders[positionKey][index] = lastOrder;
            positionOrderIndex[positionKey][lastOrderKey] = index;
        }
        delete positionOrderIndex[positionKey][orderKey];
        positionOrders[positionKey].pop();

        if (
            !order.isIncrease &&
            (order.tradeType == TradingTypes.TradeType.MARKET || order.tradeType == TradingTypes.TradeType.LIMIT)
        ) {
            positionDecreaseTotalAmount[positionKey] -= order.sizeAmount;
        }
    }

    function setPositionHasTpSl(
        bytes32 key,
        TradingTypes.TradeType tradeType,
        bool has
    ) external onlyExecutor whenNotPaused {
        positionHasTpSl[key][tradeType] = has;
    }

    function removeIncreaseMarketOrders(uint256 orderId) external onlyExecutor whenNotPaused {
        delete increaseMarketOrders[orderId];
    }

    function removeIncreaseLimitOrders(uint256 orderId) external onlyExecutor whenNotPaused {
        delete increaseLimitOrders[orderId];
    }

    function removeDecreaseMarketOrders(uint256 orderId) external onlyExecutor whenNotPaused {
        delete decreaseMarketOrders[orderId];
    }

    function removeDecreaseLimitOrders(uint256 orderId) external onlyExecutor whenNotPaused {
        delete decreaseLimitOrders[orderId];
    }

    function setOrderNeedADL(
        uint256 orderId,
        TradingTypes.TradeType tradeType,
        bool needADL
    ) external onlyExecutor whenNotPaused {
        TradingTypes.DecreasePositionOrder storage order;
        if (tradeType == TradingTypes.TradeType.MARKET) {
            order = decreaseMarketOrders[orderId];
        } else {
            order = decreaseLimitOrders[orderId];
            require(order.tradeType == tradeType, 'trade type not match');
        }
        order.needADL = needADL;
    }

    function saveOrderTpSl(bytes32 orderKey, TradingTypes.OrderWithTpSl memory tpSl) external onlyRouter whenNotPaused {
        orderWithTpSl[orderKey] = tpSl;
    }

    function removeOrderTpSl(bytes32 orderKey) external onlyExecutor whenNotPaused {
        delete orderWithTpSl[orderKey];
    }

    function setPaused() external onlyAdmin {
        _pause();
    }

    function setUnPaused() external onlyAdmin {
        _unpause();
    }
}

contracts/trading/PositionManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/security/Pausable.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/utils/math/Math.sol';

import '../libraries/Position.sol';
import '../libraries/PositionKey.sol';
import '../interfaces/IPositionManager.sol';
import '../interfaces/IOraclePriceFeed.sol';
import '../libraries/PrecisionUtils.sol';
import '../libraries/Int256Utils.sol';
import '../libraries/Roleable.sol';
import '../interfaces/IPool.sol';
import '../interfaces/IPool.sol';
import '../interfaces/IAddressesProvider.sol';
import '../interfaces/IRoleManager.sol';

contract PositionManager is IPositionManager, ReentrancyGuard, Roleable, Pausable {
    using SafeERC20 for IERC20;
    using PrecisionUtils for uint256;
    using Math for uint256;
    using Int256Utils for int256;
    using Position for mapping(bytes32 => Position.Info);
    using Position for Position.Info;

    mapping(bytes32 => Position.Info) public positions;

    mapping(address => bool) public override isFrozen;

    mapping(uint256 => int256) public override netExposureAmountChecker;
    mapping(uint256 => uint256) public override longTracker;
    mapping(uint256 => uint256) public override shortTracker;

    // cumulativeFundingRates tracks the funding rates based on utilization
    mapping(uint256 => int256) public cumulativeFundingRates;
    mapping(uint256 => int256) public lastFundingRates;
    // lastFundingTimes tracks the last time funding was updated for a token
    mapping(uint256 => uint256) public lastFundingTimes;

    uint256 public fundingInterval;

    IPool public pool;
    address public tradingFeeReceiver;
    address public addressExecutor;

    constructor(
        IAddressesProvider addressProvider,
        IPool _pairInfo,
        address _tradingFeeReceiver,
        uint256 _fundingInterval
    ) Roleable(addressProvider) {
        pool = _pairInfo;
        tradingFeeReceiver = _tradingFeeReceiver;
        fundingInterval = _fundingInterval;
    }

    modifier onlyExecutor() {
        require(msg.sender == addressExecutor, 'Position Manager: forbidden');
        _;
    }

    function setExecutor(address _addressExecutor) external onlyPoolAdmin {
        addressExecutor = _addressExecutor;
    }

    function updateTradingFeeReceiver(address newReceiver) external onlyPoolAdmin {
        address oldReceiver = tradingFeeReceiver;
        tradingFeeReceiver = newReceiver;
        emit UpdateTradingFeeReceiver(oldReceiver, newReceiver);
    }

    function updateFundingInterval(uint256 newInterval) external onlyPoolAdmin {
        uint256 oldInterval = fundingInterval;
        fundingInterval = newInterval;
        emit UpdateFundingInterval(oldInterval, newInterval);
    }

    function increasePosition(
        address _account,
        uint256 _pairIndex,
        int256 _collateral,
        uint256 _sizeAmount,
        bool _isLong,
        uint256 _price
    ) external nonReentrant onlyExecutor whenNotPaused returns (uint256 tradingFee, int256 fundingFee) {
        IPool.Pair memory pair = pool.getPair(_pairIndex);
        require(pair.enable, 'trade pair not supported');

        // get position
        bytes32 positionKey = PositionKey.getPositionKey(_account, _pairIndex, _isLong);
        Position.Info storage position = positions[positionKey];
        position.key = positionKey;

        uint256 sizeDelta = _sizeAmount.mulPrice(_price);

        // 
        if (position.positionAmount == 0) {
            position.account = _account;
            position.pairIndex = _pairIndex;
            position.isLong = _isLong;
            position.averagePrice = _price;
        }

        if (position.positionAmount > 0 && sizeDelta > 0) {
            position.averagePrice = (position.positionAmount.mulPrice(position.averagePrice) + sizeDelta).mulDiv(
                PrecisionUtils.pricePrecision(),
                (position.positionAmount + _sizeAmount)
            );
        }

        position.positionAmount = position.positionAmount + _sizeAmount;

        int256 afterCollateral = int256(position.collateral);
        uint256 transferOut;

        // funding fee
        updateCumulativeFundingRate(_pairIndex, _price);
        fundingFee = getFundingFee(true, _account, _pairIndex, _isLong, _sizeAmount);

        if (fundingFee >= 0) {
            if (_isLong) {
                afterCollateral -= fundingFee;
            } else {
                (uint256 userAmount, ) = _distributeFundingFee(_pairIndex, fundingFee.abs());
                transferOut += userAmount;
            }
        } else {
            if (!_isLong) {
                afterCollateral += fundingFee;
            } else {
                (uint256 userAmount, ) = _distributeFundingFee(_pairIndex, fundingFee.abs());
                transferOut += userAmount;
            }
        }

        position.entryFundingRate = cumulativeFundingRates[_pairIndex];
        position.entryFundingTime = lastFundingTimes[_pairIndex];

        // trading fee
        tradingFee = _tradingFee(_pairIndex, _isLong, _sizeAmount, _price);
        afterCollateral -= int256(tradingFee);

        IERC20(pair.stableToken).safeTransfer(tradingFeeReceiver, tradingFee);

        // final collateral & out
        afterCollateral += _collateral;
        transferOut += _collateral < 0 ? _collateral.abs() : 0;
        require(afterCollateral > 0, 'collateral not enough');

        position.collateral = afterCollateral.abs();

        // update lp vault
        if (_sizeAmount > 0) {
            int256 prevNetExposureAmountChecker = netExposureAmountChecker[_pairIndex];
            netExposureAmountChecker[_pairIndex] =
                prevNetExposureAmountChecker +
                (_isLong ? int256(_sizeAmount) : -int256(_sizeAmount));
            if (_isLong) {
                longTracker[_pairIndex] += _sizeAmount;
            } else {
                shortTracker[_pairIndex] += _sizeAmount;
            }

            IPool.Vault memory lpVault = pool.getVault(_pairIndex);

            if (prevNetExposureAmountChecker > 0) {
                if (netExposureAmountChecker[_pairIndex] > prevNetExposureAmountChecker) {
                    pool.increaseReserveAmount(_pairIndex, _sizeAmount, 0);

                    uint256 averagePrice = (uint256(prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) +
                        sizeDelta).calculatePrice(uint256(prevNetExposureAmountChecker) + _sizeAmount);

                    pool.updateAveragePrice(_pairIndex, averagePrice);
                } else {
                    uint256 decreaseLong;
                    uint256 increaseShort;

                    if (netExposureAmountChecker[_pairIndex] >= 0) {
                        decreaseLong = _sizeAmount;
                    } else {
                        decreaseLong = uint256(prevNetExposureAmountChecker);
                        increaseShort = _sizeAmount - decreaseLong;
                    }

                    // decrease reserve & pnl

                    pool.decreaseReserveAmount(_pairIndex, decreaseLong, 0);
                    if (_price > lpVault.averagePrice) {
                        uint256 profit = decreaseLong.mulPrice(_price - lpVault.averagePrice);

                        pool.decreaseProfit(_pairIndex, profit);
                    } else {
                        uint256 profit = decreaseLong.mulPrice(lpVault.averagePrice - _price);

                        IERC20(pair.stableToken).safeTransfer(address(pool), profit);
                        pool.increaseProfit(_pairIndex, profit);
                    }

                    // increase reserve
                    if (increaseShort > 0) {
                        pool.increaseReserveAmount(_pairIndex, 0, increaseShort.mulPrice(_price));

                        pool.updateAveragePrice(_pairIndex, _price);
                    }

                    // zero exposure
                    if (netExposureAmountChecker[_pairIndex] == 0) {
                        pool.updateAveragePrice(_pairIndex, 0);
                    }
                }
            } else if (prevNetExposureAmountChecker < 0) {
                if (netExposureAmountChecker[_pairIndex] < prevNetExposureAmountChecker) {
                    pool.increaseReserveAmount(_pairIndex, 0, sizeDelta);

                    uint256 averagePrice = (uint256(-prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) +
                        sizeDelta).calculatePrice(uint256(-prevNetExposureAmountChecker) + _sizeAmount);
                    pool.updateAveragePrice(_pairIndex, averagePrice);
                } else {
                    uint256 decreaseShort;
                    uint256 increaseLong;

                    if (netExposureAmountChecker[_pairIndex] <= 0) {
                        decreaseShort = _sizeAmount;
                    } else {
                        decreaseShort = uint256(-prevNetExposureAmountChecker);
                        increaseLong = _sizeAmount - decreaseShort;
                    }

                    // decrease reserve & pnl
                    pool.decreaseReserveAmount(
                        _pairIndex,
                        0,
                        netExposureAmountChecker[_pairIndex] >= 0
                            ? lpVault.stableReservedAmount
                            : decreaseShort.mulPrice(lpVault.averagePrice)
                    );
                    if (_price > lpVault.averagePrice) {
                        uint256 profit = decreaseShort.mulPrice(_price - lpVault.averagePrice);
                        IERC20(pair.stableToken).safeTransfer(address(pool), profit);
                        pool.increaseProfit(_pairIndex, profit);
                    } else {
                        uint256 profit = decreaseShort.mulPrice(lpVault.averagePrice - _price);
                        pool.decreaseProfit(_pairIndex, profit);
                    }

                    // increase reserve
                    if (increaseLong > 0) {
                        pool.increaseReserveAmount(_pairIndex, increaseLong, 0);
                        pool.updateAveragePrice(_pairIndex, _price);
                    }

                    // zero exposure
                    if (netExposureAmountChecker[_pairIndex] == 0) {
                        pool.updateAveragePrice(_pairIndex, 0);
                    }
                }
            } else {
                if (netExposureAmountChecker[_pairIndex] > 0) {
                    pool.increaseReserveAmount(_pairIndex, _sizeAmount, 0);
                } else {
                    pool.increaseReserveAmount(_pairIndex, 0, sizeDelta);
                }
                pool.updateAveragePrice(_pairIndex, _price);
            }
        }

        if (transferOut > 0) {
            IERC20(pair.stableToken).safeTransfer(_account, transferOut);
        }

        emit IncreasePosition(
            positionKey,
            _account,
            _pairIndex,
            _collateral,
            _isLong,
            _sizeAmount,
            _price,
            tradingFee,
            fundingFee,
            transferOut
        );

        emit UpdatePosition(
            positionKey,
            _account,
            _pairIndex,
            _isLong,
            position.collateral,
            position.positionAmount,
            position.averagePrice,
            position.entryFundingRate,
            position.entryFundingTime,
            position.realisedPnl,
            _price
        );
    }

    function decreasePosition(
        address _account,
        uint256 _pairIndex,
        int256 _collateral,
        uint256 _sizeAmount,
        bool _isLong,
        uint256 _price
    ) external onlyExecutor nonReentrant whenNotPaused returns (uint256 tradingFee, int256 fundingFee, int256 pnl) {
        IPool.Pair memory pair = pool.getPair(_pairIndex);

        // check trading amount
        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(_pairIndex);
        require(
            _sizeAmount >= tradingConfig.minTradeAmount && _sizeAmount <= tradingConfig.maxTradeAmount,
            'invalid size'
        );

        // get position
        bytes32 positionKey = PositionKey.getPositionKey(_account, _pairIndex, _isLong);
        Position.Info storage position = positions[positionKey];
        require(position.account != address(0), 'position already closed');

        // update position size
        uint256 sizeDelta = _sizeAmount.mulPrice(_price);

        position.positionAmount -= _sizeAmount;

        int256 afterCollateral = int256(position.collateral);
        uint256 transferOut;

        // funding fee
        updateCumulativeFundingRate(_pairIndex, _price);
        fundingFee = getFundingFee(false, _account, _pairIndex, _isLong, _sizeAmount);

        if (fundingFee >= 0) {
            if (_isLong) {
                afterCollateral -= fundingFee;
            } else {
                (uint256 userAmount, ) = _distributeFundingFee(_pairIndex, fundingFee.abs());
                transferOut += userAmount;
            }
        } else {
            if (!_isLong) {
                afterCollateral -= (-fundingFee);
            } else {
                (uint256 userAmount, ) = _distributeFundingFee(_pairIndex, fundingFee.abs());
                transferOut += userAmount;
            }
        }

        position.entryFundingRate = cumulativeFundingRates[_pairIndex];
        position.entryFundingTime = lastFundingTimes[_pairIndex];

        // trading fee
        tradingFee = _tradingFee(_pairIndex, !_isLong, _sizeAmount, _price);
        afterCollateral -= int256(tradingFee);

        IERC20(pair.stableToken).safeTransfer(tradingFeeReceiver, tradingFee);

        // update lp vault
        if (_sizeAmount > 0) {
            int256 prevNetExposureAmountChecker = netExposureAmountChecker[_pairIndex];
            netExposureAmountChecker[_pairIndex] =
                prevNetExposureAmountChecker +
                (_isLong ? -int256(_sizeAmount) : int256(_sizeAmount));
            if (_isLong) {
                longTracker[_pairIndex] -= _sizeAmount;
            } else {
                shortTracker[_pairIndex] -= _sizeAmount;
            }

            IPool.Vault memory lpVault = pool.getVault(_pairIndex);
            if (prevNetExposureAmountChecker > 0) {
                if (netExposureAmountChecker[_pairIndex] > prevNetExposureAmountChecker) {
                    pool.increaseReserveAmount(_pairIndex, _sizeAmount, 0);

                    uint256 averagePrice = (uint256(prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) +
                        sizeDelta).calculatePrice(uint256(prevNetExposureAmountChecker) + _sizeAmount);
                    pool.updateAveragePrice(_pairIndex, averagePrice);
                } else {
                    uint256 decreaseLong;
                    uint256 increaseShort;

                    if (netExposureAmountChecker[_pairIndex] >= 0) {
                        decreaseLong = _sizeAmount;
                    } else {
                        decreaseLong = uint256(prevNetExposureAmountChecker);
                        increaseShort = _sizeAmount - decreaseLong;
                    }

                    // decrease reserve & pnl
                    pool.decreaseReserveAmount(_pairIndex, decreaseLong, 0);
                    if (_price > lpVault.averagePrice) {
                        uint256 profit = decreaseLong.mulPrice(_price - lpVault.averagePrice);
                        pool.decreaseProfit(_pairIndex, profit);
                    } else {
                        uint256 profit = decreaseLong.mulPrice(lpVault.averagePrice - _price);
                        IERC20(pair.stableToken).safeTransfer(address(pool), profit);
                        pool.increaseProfit(_pairIndex, profit);
                    }

                    // increase reserve
                    if (increaseShort > 0) {
                        pool.increaseReserveAmount(_pairIndex, 0, increaseShort.mulPrice(_price));
                        pool.updateAveragePrice(_pairIndex, _price);
                    }

                    // zero exposure
                    if (netExposureAmountChecker[_pairIndex] == 0) {
                        pool.updateAveragePrice(_pairIndex, 0);
                    }
                }
            } else if (prevNetExposureAmountChecker < 0) {
                if (netExposureAmountChecker[_pairIndex] < prevNetExposureAmountChecker) {
                    pool.increaseReserveAmount(_pairIndex, 0, sizeDelta);

                    uint256 averagePrice = (uint256(-prevNetExposureAmountChecker).mulPrice(lpVault.averagePrice) +
                        sizeDelta).calculatePrice(uint256(-prevNetExposureAmountChecker) + _sizeAmount);
                    pool.updateAveragePrice(_pairIndex, averagePrice);
                } else {
                    uint256 decreaseShort;
                    uint256 increaseLong;

                    if (netExposureAmountChecker[_pairIndex] <= 0) {
                        decreaseShort = _sizeAmount;
                    } else {
                        decreaseShort = uint256(-prevNetExposureAmountChecker);
                        increaseLong = _sizeAmount - decreaseShort;
                    }

                    // decrease reserve & pnl

                    pool.decreaseReserveAmount(
                        _pairIndex,
                        0,
                        netExposureAmountChecker[_pairIndex] >= 0
                            ? lpVault.stableReservedAmount
                            : decreaseShort.mulPrice(lpVault.averagePrice)
                    );
                    if (_price > lpVault.averagePrice) {
                        uint256 profit = decreaseShort.mulPrice(_price - lpVault.averagePrice);

                        IERC20(pair.stableToken).safeTransfer(address(pool), profit);
                        pool.increaseProfit(_pairIndex, profit);
                    } else {
                        uint256 profit = decreaseShort.mulPrice(lpVault.averagePrice - _price);

                        pool.decreaseProfit(_pairIndex, profit);
                    }

                    // increase reserve
                    if (increaseLong > 0) {
                        pool.increaseReserveAmount(_pairIndex, increaseLong, 0);

                        pool.updateAveragePrice(_pairIndex, _price);
                    }

                    // zero exposure
                    if (netExposureAmountChecker[_pairIndex] == 0) {
                        pool.updateAveragePrice(_pairIndex, 0);
                    }
                }
            } else {
                if (netExposureAmountChecker[_pairIndex] > 0) {
                    pool.increaseReserveAmount(_pairIndex, _sizeAmount, 0);
                } else {
                    pool.increaseReserveAmount(_pairIndex, 0, sizeDelta);
                }
                pool.updateAveragePrice(_pairIndex, _price);
            }
        }

        // pnl
        uint256 price = IOraclePriceFeed(ADDRESS_PROVIDER.getPriceOracle()).getPrice(pair.indexToken);
        pnl = position.getUnrealizedPnl(_sizeAmount, price);

        if (pnl > 0) {
            transferOut += pnl.abs();
        } else {
            afterCollateral += pnl;
        }
        position.realisedPnl += pnl;

        // final collateral & out
        if (position.positionAmount == 0) {
            // transfer out all collateral and _collateral
            int256 allTransferOut = int256(transferOut) + afterCollateral + (_collateral > 0 ? _collateral : int256(0));
            transferOut = allTransferOut > 0 ? allTransferOut.abs() : 0;
            delete positions[positionKey];
            emit ClosePosition(positionKey, _account, _pairIndex, _isLong);
        } else {
            afterCollateral += _collateral;
            transferOut += (_collateral < 0 ? uint256(-_collateral) : 0);
            require(afterCollateral > 0, 'collateral not enough');
            position.collateral = afterCollateral.abs();
        }

        if (transferOut > 0) {
            IERC20(pair.stableToken).safeTransfer(_account, transferOut);
        }

        emit DecreasePosition(
            positionKey,
            _account,
            _pairIndex,
            _isLong,
            _collateral,
            _sizeAmount,
            _price,
            tradingFee,
            fundingFee,
            pnl,
            transferOut
        );

        emit UpdatePosition(
            positionKey,
            _account,
            _pairIndex,
            _isLong,
            position.collateral,
            position.positionAmount,
            position.averagePrice,
            position.entryFundingRate,
            position.entryFundingTime,
            position.realisedPnl,
            _price
        );
    }

    function updateCumulativeFundingRate(uint256 _pairIndex, uint256 _price) public whenNotPaused {
        if (lastFundingTimes[_pairIndex] == 0) {
            lastFundingTimes[_pairIndex] = (block.timestamp / fundingInterval) * fundingInterval;
            return;
        }

        if (block.timestamp - lastFundingTimes[_pairIndex] < fundingInterval) {
            return;
        }

        uint256 intervals = (block.timestamp - lastFundingTimes[_pairIndex]) / fundingInterval;
        int256 nextFundingRate = _currentFundingRate(_pairIndex, _price);

        lastFundingRates[_pairIndex] = nextFundingRate;
        cumulativeFundingRates[_pairIndex] = cumulativeFundingRates[_pairIndex] + nextFundingRate * int256(intervals);
        lastFundingTimes[_pairIndex] = (block.timestamp / fundingInterval) * fundingInterval;

        emit UpdateFundingRate(_pairIndex, cumulativeFundingRates[_pairIndex], lastFundingTimes[_pairIndex]);
    }

    function getTradingFee(
        uint256 _pairIndex,
        bool _isLong,
        uint256 _sizeAmount
    ) external view override returns (uint256 tradingFee) {
        IPool.Pair memory pair = pool.getPair(_pairIndex);
        uint256 price = IOraclePriceFeed(ADDRESS_PROVIDER.getPriceOracle()).getPrice(pair.indexToken);
        return _tradingFee(_pairIndex, _isLong, _sizeAmount, price);
    }

    function _tradingFee(
        uint256 _pairIndex,
        bool _isLong,
        uint256 _sizeAmount,
        uint256 _price
    ) internal view returns (uint256 tradingFee) {
        uint256 sizeDelta = _sizeAmount.mulPrice(_price);

        IPool.TradingFeeConfig memory tradingFeeConfig = pool.getTradingFeeConfig(_pairIndex);
        if (netExposureAmountChecker[_pairIndex] >= 0) {
            if (_isLong) {
                // fee
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.takerFeeP);
            } else {
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.makerFeeP);
            }
        } else {
            if (_isLong) {
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.makerFeeP);
            } else {
                tradingFee = sizeDelta.mulPercentage(tradingFeeConfig.takerFeeP);
            }
        }
        return tradingFee;
    }

    //TODO will remove
    function transferTokenTo(address token, address to, uint256 amount) external onlyExecutor {
        IERC20(token).safeTransfer(to, amount);
    }

    function getFundingFee(
        bool _increase,
        address _account,
        uint256 _pairIndex,
        bool _isLong,
        uint256 _sizeAmount
    ) public view override returns (int256) {
        Position.Info memory position = positions.get(_account, _pairIndex, _isLong);

        uint256 interval = block.timestamp - position.entryFundingTime;
        if (interval < fundingInterval) {
//            if (!_increase) {
//                int256 fundingRate = (lastFundingRates[_pairIndex] * int256(interval)) / int256(fundingInterval);
//                return (int256(_sizeAmount) * fundingRate) / int256(PrecisionUtils.fundingRatePrecision());
//            }
            return 0;
        }

        int256 fundingRate = cumulativeFundingRates[_pairIndex] - position.entryFundingRate;
        return (int256(position.positionAmount) * fundingRate) / int256(PrecisionUtils.fundingRatePrecision());
    }

    function getCurrentFundingRate(uint256 _pairIndex) external view override returns (int256) {
        IPool.Pair memory pair = pool.getPair(_pairIndex);
        uint256 price = IOraclePriceFeed(ADDRESS_PROVIDER.getPriceOracle()).getPrice(pair.indexToken);
        return _currentFundingRate(_pairIndex, price);
    }

    function _currentFundingRate(uint256 _pairIndex, uint256 _price) internal view returns (int256 fundingRate) {
        IPool.FundingFeeConfig memory fundingFeeConfig = pool.getFundingFeeConfig(_pairIndex);

        uint256 absNetExposure = netExposureAmountChecker[_pairIndex].abs();
        uint256 w = fundingFeeConfig.fundingWeightFactor;
        uint256 q = longTracker[_pairIndex] + shortTracker[_pairIndex];
        uint256 k = fundingFeeConfig.liquidityPremiumFactor;

        IPool.Vault memory lpVault = pool.getVault(_pairIndex);
        uint256 l = (lpVault.indexTotalAmount - lpVault.indexReservedAmount).mulPrice(_price) +
            (lpVault.stableTotalAmount - lpVault.stableReservedAmount);

        uint256 absFundingRate;
        if (q == 0 || l == 0) {
            fundingRate = fundingFeeConfig.defaultFundingRate;
        } else {
            absFundingRate =
                (w * absNetExposure * PrecisionUtils.fundingRatePrecision()) /
                (k * q) +
                ((PrecisionUtils.fundingRatePrecision() - w) * absNetExposure) /
                (k * l);
            fundingRate = netExposureAmountChecker[_pairIndex] >= 0 ? int256(absFundingRate) : -int256(absFundingRate);
        }

        fundingRate = (fundingRate - fundingFeeConfig.interest).max(fundingFeeConfig.minFundingRate).min(
            fundingFeeConfig.maxFundingRate
        );
    }

    function _distributeFundingFee(
        uint256 _pairIndex,
        uint256 _fundingFee
    ) internal returns (uint256 userAmount, uint256 lpAmount) {
        IPool.Pair memory pair = pool.getPair(_pairIndex);
        IPool.FundingFeeConfig memory fundingFeeConfig = pool.getFundingFeeConfig(_pairIndex);

        lpAmount = _fundingFee.mulPercentage(fundingFeeConfig.lpDistributeP);
        userAmount = _fundingFee - lpAmount;

        IERC20(pair.stableToken).safeTransfer(address(pool), lpAmount);
        pool.increaseTotalAmount(_pairIndex, 0, lpAmount);

        return (userAmount, lpAmount);
    }

    function getValidPrice(address token, uint256 _pairIndex, bool _isLong) public view returns (uint256) {
        IOraclePriceFeed oraclePriceFeed = IOraclePriceFeed(ADDRESS_PROVIDER.getPriceOracle());

        // IPool.Pair memory pair = pool.getPair(_pairIndex);
        uint256 oraclePrice = oraclePriceFeed.getPrice(token);

        uint256 indexPrice = oraclePriceFeed.getIndexPrice(token, 0);

        uint256 diffP = oraclePrice > indexPrice ? oraclePrice - indexPrice : indexPrice - oraclePrice;
        diffP = diffP.calculatePercentage(oraclePrice);

        IPool.TradingConfig memory tradingConfig = pool.getTradingConfig(_pairIndex);
        require(diffP <= tradingConfig.maxPriceDeviationP, 'exceed max price deviation');
        return oraclePrice;
    }

    function getPosition(
        address _account,
        uint256 _pairIndex,
        bool _isLong
    ) public view returns (Position.Info memory) {
        Position.Info memory position = positions.get(_account, _pairIndex, _isLong);
        return position;
    }

    function getPositionByKey(bytes32 key) public view returns (Position.Info memory) {
        Position.Info memory position = positions[key];
        return position;
    }

    function getPositionKey(address _account, uint256 _pairIndex, bool _isLong) public pure returns (bytes32) {
        return PositionKey.getPositionKey(_account, _pairIndex, _isLong);
    }

    function setPaused() external onlyAdmin {
        _pause();
    }

    function setUnPaused() external onlyAdmin {
        _unpause();
    }
}

