        int256 sizeAmount; // size
        uint256 tpPrice; // 1e30, tp price
        uint256 tp; // tp size
        uint256 slPrice; // 1e30, sl price
        uint256 sl; // sl size
    }

    struct IncreasePositionRequest {
        address account;
        uint256 pairIndex; // pair index
